settings.rowStyleFunction:
function rstyleData(){
    if(entity.comError == "false"){
        if(entity.deltaUnitFault == "true"){
            return {"background-color" : "#ef7d00"};
        }else{
            if(entity.isConsuming == "false"){
                return {"background-color" : "#afca0b"};
            }else{
                return {"background-color" : "#50af47"};
            }
        }
    } else {
        return {"background-color" : "#303030"};
    }
}
x=rstyleData();
x['cursor']='pointer';
return x;

settings.cellContentFunction:
let e=entity;

let msg = [];

if(e.comError=='true'){
    return "Com.Error";
} else if(e.badData=='true'){
    return "BadData";
} else if(e.fmDataCorrupted=='true'){
    return "DataCorrupted";
} else if(e.deltaUnitFault=='true'){
    return "DeltaUnitFault";
}



// if(e.voltageInRange=='false'){
