// } 


if(e.isConsuming=='false') {
    msg.push("NoConsumption");
} 
// else {
    
//     if(e.em3PhUnbalancedLoad=='true'){
//         msg.push("3PhLoadUnb.");
//     }
    
//     if(e.emCapacitiveLoad=='true'){
//         msg.push("Cap.Load");
//     }
// }


if(msg.length>0){
    return msg.join(", ");
}else{
    return "All over okay"
}

