settings.tooltipValueFormatter:
let x = value.toFixed(3);
let parts = x.toString().split(".");
parts[0] = parts[0].replace(/\B(?=(\d{3})+(?!\d))/g, ",");
x = parts.join(".");
return x + " Lit/h";

