descriptor.controllerScript:
self.onInit = function() {
    const $scope=self.ctx.$scope;
    $scope.displayedColumns=['year', 'action'];
    years=[];
    $scope.dataSource=[];
    for(let i=Number(moment().format('YYYY'))-25; i<Number(moment().format('YYYY'))+25; i++){
        years.push(i);
    }
    $scope.selectedYear=Number(moment().format('YYYY'))
    $scope.years=years;
    $scope.showForm=false;
    const $injector = $scope.$injector;
    const customDialog = $injector.get(self.ctx.servicesMap.get('customDialog'));
    $scope.form=$scope.fb.group({
        processedVolume: ['',$scope.validators.required],
        furnaceOilConsumption: ['',$scope.validators.required],
        furnaceOilConsumptionPerKG: ['',$scope.validators.required],
        totalGeneratedSteam: ['',$scope.validators.required],
        temparature: ['',$scope.validators.required],
        steamConsumedLine1: ['',$scope.validators.required],
        steamConsumedLine2: ['',$scope.validators.required],
        purchasedElectricity: ['',$scope.validators.required],
        generatedElectricity: ['',$scope.validators.required],
        solarElectricity: ['',$scope.validators.required],
        electricityConsumtionPerKG: ['',$scope.validators.required],
        electricityConsumtionLine1: ['',$scope.validators.required],
        electricityConsumtionLine2: ['',$scope.validators.required],
        electricityConsumtionOffice: ['',$scope.validators.required],
        electricityConsumtionCompressor: ['',$scope.validators.required],
        compressedAir: ['',$scope.validators.required],
        dieselConsumption: ['',$scope.validators.required],
        generatorRuntime: ['',$scope.validators.required],
        loadSheddingTime: ['',$scope.validators.required],
        dieselConsumeGen1PerHr: ['',$scope.validators.required],
        dieselConsumeGen2PerHr: ['',$scope.validators.required],
        dieselConsumeGen3PerHr: ['',$scope.validators.required],
        dieselConsumeGen4PerHr: ['',$scope.validators.required],
        dieselConsumeGen5PerHr: ['',$scope.validators.required],
        waterWithdrawn: ['',$scope.validators.required],
        waterWithdrawnPerKG: ['',$scope.validators.required],
        waterRecycled: ['',$scope.validators.required],
        waterRecycledPercentage: ['',$scope.validators.required],
        waterDischarged: ['',$scope.validators.required],
        processedWaterConsumption: ['',$scope.validators.required],
        socialWaterConsumption: ['',$scope.validators.required],
        boilerWaterConsumption: ['',$scope.validators.required],
        waterInflowToEtp: ['',$scope.validators.required],
    })

    $scope.submitForm=function(){
        if($scope.form.valid){
            let indx=-1;
            let objArr=$scope.dataSource.filter(dsource=>dsource.year==$scope.selectedYear);
            if(objArr){
                indx=$scope.dataSource.indexOf(objArr[0]);
            }
            if(indx!=-1){
                $scope.dataSource[indx].target=$scope.form.value;
            }else{
                $scope.dataSource.push({year: $scope.selectedYear, target: $scope.form.value});
            }
            $scope.dataSource.sort((a,b)=>a.year-b.year)
            self.ctx.attributeService.saveEntityAttributes(self.ctx.data[0].datasource.entity.id, 'SERVER_SCOPE', [{key: self.ctx.data[0].dataKey.name, value: $scope.dataSource}]).subscribe(res=>{
                self.ctx.showToast('success', 'Target for '+$scope.selectedYear+' is saved!', 2000, 'top', 'start',  self.ctx.$scope.toastTargetId);
                $scope.showForm=false;
                self.ctx.detectChanges();
            });
        }
    }
    $scope.openTargetPopUp=function(targetData={year: moment().format('YYYY'),  target: {}}, type='add'){
        $scope.showForm=true;
        let sYear=Number(targetData.year);
        if(type=='add'){
            yearList=[];
            $scope.dataSource.forEach(dt=>{
                if(yearList.indexOf(Number(dt.year))==-1){
                    yearList.push(Number(dt.year));
                }
            });
            yearList.sort();
            sYear=yearList[yearList.length-1]+1;
        }
        $scope.selectedYear=sYear;
        const data=targetData.target;
        $scope.form.patchValue({
            processedVolume: data.processedVolume,
            furnaceOilConsumption: data.furnaceOilConsumption,
            furnaceOilConsumptionPerKG: data.furnaceOilConsumptionPerKG,
            totalGeneratedSteam: data.totalGeneratedSteam,
            temparature: data.temparature,
            steamConsumedLine1: data.steamConsumedLine1,
            steamConsumedLine2: data.steamConsumedLine2,
            purchasedElectricity: data.purchasedElectricity,
            generatedElectricity: data.generatedElectricity,
            solarElectricity: data.solarElectricity,
            electricityConsumtionPerKG: data.electricityConsumtionPerKG,
            electricityConsumtionLine1: data.electricityConsumtionLine1,
            electricityConsumtionLine2: data.electricityConsumtionLine2,
            electricityConsumtionOffice: data.electricityConsumtionOffice,
            electricityConsumtionCompressor: data.electricityConsumtionCompressor,
            compressedAir: data.compressedAir,
            dieselConsumption: data.dieselConsumption,
            generatorRuntime: data.generatorRuntime,
            loadSheddingTime: data.loadSheddingTime,
            dieselConsumeGen1PerHr: data.dieselConsumeGen1PerHr,
            dieselConsumeGen2PerHr: data.dieselConsumeGen2PerHr,
            dieselConsumeGen3PerHr: data.dieselConsumeGen3PerHr,
            dieselConsumeGen4PerHr: data.dieselConsumeGen4PerHr,
            dieselConsumeGen5PerHr: data.dieselConsumeGen5PerHr,
            waterWithdrawn: data.waterWithdrawn,
            waterWithdrawnPerKG: data.waterWithdrawnPerKG,
            waterRecycled: data.waterRecycled,
            waterRecycledPercentage: data.waterRecycledPercentage,
            waterDischarged: data.waterDischarged,
            processedWaterConsumption: data.processedWaterConsumption,
            socialWaterConsumption: data.socialWaterConsumption,
            boilerWaterConsumption: data.boilerWaterConsumption,
            waterInflowToEtp: data.waterInflowToEtp,
        });
        self.ctx.detectChanges();
    }
    $scope.closeForm=function(){
        $scope.showForm=false;
        $scope.selectedYear='';
        $scope.form.reset();
        self.ctx.detectChanges();
    }
    $scope.changeYear=function(event){
        if($scope.dataSource.length){
            let objArr=$scope.dataSource.filter(dsource=>dsource.year==event.value);
            if(objArr.length){
                $scope.openTargetPopUp(objArr[0]);
            }else{
                $scope.openTargetPopUp({year: event.value, target: {}});
            }
        }else{
            $scope.openTargetPopUp({year: event.value, target: {}});
        }
    }
    self.onResize();
}

self.onDataUpdated = function() {
    const $scope=self.ctx.$scope;
    if(self.ctx.data.length){
        if(self.ctx.data[0].data.length){
            let datastring=self.ctx.data[0].data[0][1];
            data={};
            try{
                data=JSON.parse(datastring)
                $scope.dataSource=data;
            }catch{

            }
            self.ctx.detectChanges();
        }
    }
}

self.onResize = function() {
    const $scope=self.ctx.$scope;
    $scope.height=self.ctx.height;
    $scope.width=self.ctx.width;
    self.ctx.detectChanges();
}

self.onDestroy = function() {
}

