descriptor.templateCss:
#enerconDDsContainer{
    width: 100%;
    overflow: auto;
}

#enerconDDsContainer::-webkit-scrollbar {
  width: 11px;
  height: 11px;
}
#enerconDDsContainer {
  scrollbar-width: thin;
  scrollbar-color: #90A4AE #CFD8DC;
}
#enerconDDsContainer::-webkit-scrollbar-track {
  background: #CFD8DC;
}
#enerconDDsContainer::-webkit-scrollbar-thumb {
  background-color: #90A4AE ;
  border-radius: 6px;
  border: 3px solid #CFD8DC;
}
/*Board table css*/
.board-table{
    border-collapse: collapse;
}
.board-table tr td{
    border: 0.5px solid gray;
    text-align: center;
}
.board-table tbody tr td:nth-child(-n+1){
    text-align: left;
}




/*Main css start for this widget*/
table.satl-device-info-form-table{
    border-collapse: collapse;
    width: 100%;
}

table tr td input{
    border: 0;
    width: 100%;
}
table.satl-device-info-form-table tbody tr td,
table.satl-device-info-form-table thead tr th{
    border:0.5px solid;
}
table thead{
    background-color: #0070C0;
    color: white;
}
td.day:hover {
    background: #003860!important;
}

