descriptor.controllerScript:
self.onInit = function() {
    const $scope=self.ctx.$scope;
    $scope.yearForm={cYear: Number(moment().format('YYYY'))};
    $scope.years=[];
    for(let i=2023; i<=Number(moment().format('YYYY')); i++){
        $scope.years.push(i);
    }
    const header={headers:{"Content-Type":"application/json","X-Authorization":"Bearer "+localStorage.getItem('jwt_token')}};
    $scope.topLossActionLogForm=$scope.fb.group({
        date: [moment().format('YYYY-MM-DD')],
        topLoss:$scope.fb.array([]),
        actionLog: $scope.fb.array([])
    });
    let topLossActionLog=[];
    $scope.settings=self.ctx.settings;
    $scope.settings.borderColor={borderColor:'gray'};
    $scope.days=[];
    $scope.showProgress=true;
    let ddsData=[];
    let enerconData=[];
    self.ctx.endDate=new Date();
    self.ctx.generateDayFromYear=function(endDate){
        $scope.days=[];
        for(i=new Date(moment(endDate).format('YYYY-01-01 06:00:00')); i<=endDate;){
            $scope.days.push({ts: i.getTime(), date: moment(new Date(i)).format('YYYY-MM-DD'), displayedDate: moment(i).format('DD-MMM')});
            i.setDate(i.getDate()+1);
        }
    }
    self.ctx.endDate.setDate(self.ctx.endDate.getDate()-1);
    self.ctx.generateDayFromYear(self.ctx.endDate);
    self.ctx.initiateEnerconOperation=function(){
        try{
            enerconData=JSON.parse(self.ctx.settings.jsonSchema);
            if(enerconData){
                resolveAttributeData()
            }
        }catch{

        }
    }
    self.ctx.initiateEnerconOperation();

    function resolveAttributeData(){
        self.ctx.attributeService.getEntityAttributes({"entityType": "ASSET","id": "cb3787e0-4631-11ee-8bc6-01b3484f4adf"},'SERVER_SCOPE', ['enerconTargetData', 'enerconYeTargetData']).subscribe(response=>{
            if(response.length){
                let yeTargetData={};
                let targetData={};
                let yeTarget=response.filter(res=>res.key=='enerconYeTargetData');

                if(yeTarget.length){
                    let targetArray=yeTarget[0].value;
                    targetObj=targetArray.filter(tarr=>tarr.year==moment(self.ctx.endDate).format('YYYY'));
                    if(targetObj.length){
                        yeTargetData=targetObj[0].target;
                    }
                }

                let ctarget=response.filter(res=>res.key=='enerconTargetData');
                if(ctarget.length){
                    let targetArray=ctarget[0].value;
                    targetObj=targetArray.filter(tarr=>tarr.year==moment(self.ctx.endDate).format('YYYY'));
                    if(targetObj.length){
                        targetData=targetObj[0].target;
                    }
                }
                $scope.loadTopLossForSelectedDate({date: moment(self.ctx.endDate).format('YYYY-MM-DD')});
                initiateDataWithTarget({yeTarget: yeTargetData, target: targetData});
            }else{
                initiateDataWithTarget();
            }
        });
    }
    $scope.loadTopLossForSelectedDate=function(day){
        $scope.topLossActionLogForm.get('date').setValue(day.date);
        self.ctx.attributeService.getEntityTimeseries({entityType: 'ASSET', id: 'cb3787e0-4631-11ee-8bc6-01b3484f4adf'}, ['topLoss','actionLog'], new Date(moment().format(day.date+' 00:00:00')).getTime(), new Date(moment().format(day.date+' 23:59:59')).getTime(), 100, 'NONE', 0, 'ASC', false).subscribe(response=>{
                    if(Object.keys(response).length){
                        actionLog=JSON.parse(response.actionLog[0].value);
                        topLoss=JSON.parse(response.topLoss[0].value);
                        patchTopLossActionLogForm({date: moment(new Date(response.actionLog[0].ts)).format('YYYY-MM-DD'), topLoss: topLoss, actionLog:actionLog})
                    }else{
                        patchTopLossActionLogFormWithNoData();
                    }
                }, error=>{
                    patchTopLossActionLogFormWithNoData();
                });
    }
    function patchTopLossActionLogFormWithNoData(){
        topLossFArray=$scope.fb.array([]);
        topLossFArray.push($scope.fb.group({
            action: [''],
            initialRootCause: [''],
            dms: ['']
        }));
        $scope.topLossActionLogForm.setControl('topLoss', topLossFArray);
        actionLogFArray=$scope.fb.array([]);
        actionLogFArray.push($scope.fb.group({
            action: [''],
            responsibility: [''],
            timeline: ['']
        }));
        $scope.topLossActionLogForm.setControl('actionLog', actionLogFArray);
        self.ctx.detectChanges();
    }
    function patchTopLossActionLogForm(obj){
        topLossFArray=$scope.fb.array([]);
        obj.topLoss.forEach(tl=>{
            topLossFArray.push($scope.fb.group({
                action: [tl.action],
                initialRootCause: [tl.initialRootCause],
                dms: [tl.dms]
            }));
        });
        $scope.topLossActionLogForm.setControl('topLoss', topLossFArray);
        actionLogFArray=$scope.fb.array([]);
        obj.actionLog.forEach(al=>{
            actionLogFArray.push($scope.fb.group({
                action: [al.action],
                responsibility: [al.responsibility],
                timeline: [al.timeline]
            }));
        });
        $scope.topLossActionLogForm.setControl('actionLog', actionLogFArray);
        self.ctx.detectChanges();
    }
    $scope.addNewFieldToTopLossActionLog=function(position){
        if(position=='topLoss'){
            $scope.topLossActionLogForm.get('topLoss').push($scope.fb.group({
                action: [''],
                initialRootCause: [''],
                dms: ['']
            }));
        }else{
            $scope.topLossActionLogForm.get('actionLog').push($scope.fb.group({
                action: [''],
                responsibility: [''],
                timeline: ['']
            }));
        }
    }
    $scope.removeTopLossActionLogRow=function(what, index){
        $scope.topLossActionLogForm.get(what).removeAt(index);
        self.ctx.detectChanges();
        $scope.submitTopLossForm();
    }
    $scope.submitTopLossForm=function(){
        postData={
            ts: new Date($scope.topLossActionLogForm.value.date).getTime(),
            values:{
                topLoss:JSON.stringify($scope.topLossActionLogForm.value.topLoss),
                actionLog: JSON.stringify($scope.topLossActionLogForm.value.actionLog)
            }
        }
        self.ctx.http.post('/api/plugins/telemetry/ASSET/cb3787e0-4631-11ee-8bc6-01b3484f4adf/timeseries/telemetry',postData,header).subscribe(response=>{
            self.ctx.showToast('success', 'Top loss and action log is updated', 2000, 'top', 'start',  self.ctx.$scope.toastTargetId)
        });
    }
    async function initiateDataWithTarget(target={}){
        if(target.target){
            enerconData.forEach(dds=>{
                if(dds.type=='data'){
                    dds['target']=target.target[dds.key]==undefined?0:target.target[dds.key];
                    dds['ye_target']=target.yeTarget[dds.key]==undefined?0:target.yeTarget[dds.key];
                    dds['day_data']=[];
                }
            });
        }

        await loadProcessedVolume();
        let temperatureDevice=[{device_temp1: '331d8350-ee2b-11ed-afaa-333332abe82d', response:[]}];
        feedWaterTemperatureResolve(new Date(moment(self.ctx.endDate).format('YYYY-01-01 00:00:00')).getTime(), self.ctx.endDate.getTime(), temperatureDevice, 0, temperatureDevice.length, 'temparature');

        let furnacedvc=[
            {type: 'in', device_1in_furnace: 'f35cf680-dce9-11ed-afaa-333332abe82d', gtype: 'furnaceOilConsumption', response:[]}, 
            {type: 'in', device_2in_furnace: 'f5cd3330-dce9-11ed-afaa-333332abe82d', gtype: 'furnaceOilConsumption', response:[]}, 
            {type: 'rtn', device_1rnt_furnace: 'f4950150-dce9-11ed-afaa-333332abe82d', gtype: 'furnaceOilConsumption', response:[]}, 
            {type: 'rtn', device_2rtn_furnace: 'f7976be0-dce9-11ed-afaa-333332abe82d', gtype: 'furnaceOilConsumption', response:[]},


            {device_wzpdcl: 'cd9b7fa0-d5cf-11ed-afaa-333332abe82d', name: 'WZPDCL', key:'purchasedElectricity', gtype: 'electricity', response:[]},
            {device_gen1: '6181dbf0-d068-11ed-8b01-738772f9cc16', name: 'Generator 1', key:'generatedElectricity', gtype: 'electricity', response:[]},
            {device_gen2: '6349cab0-d068-11ed-8b01-738772f9cc16', name: 'Generator 2', key:'generatedElectricity', gtype: 'electricity', response:[]},
            {device_gen3: '6513dc50-d068-11ed-8b01-738772f9cc16', name: 'Generator 3', key:'generatedElectricity', gtype: 'electricity', response:[]},
            {device_gen4: '66de1500-d068-11ed-8b01-738772f9cc16', name: 'Generator 4', key:'generatedElectricity', gtype: 'electricity', response:[]},
            {device_gen5: 'dd86f7f0-d5cf-11ed-afaa-333332abe82d', name: 'Generator 5', key:'generatedElectricity', gtype: 'electricity', response:[]},
            {device_solar1: 'f3471620-1d77-11ee-8bc6-01b3484f4adf', name: 'Solar 1', key:'solarElectricity', gtype: 'electricity', response:[]},
            {device_solar2: 'f31298a0-1d77-11ee-8bc6-01b3484f4adf', name: 'Solar 2', key:'solarElectricity', gtype: 'electricity', response:[]},
            {device_solar3: 'f37ddd90-1d77-11ee-8bc6-01b3484f4adf', name: 'Solar 3', key:'solarElectricity', gtype: 'electricity', response:[]},
            {device_solar4: 'f334edb0-1d77-11ee-8bc6-01b3484f4adf', name: 'Solar 4', key:'solarElectricity', gtype: 'electricity', response:[]},
            {device_solar5: 'f3236180-1d77-11ee-8bc6-01b3484f4adf', name: 'Solar 5', key:'solarElectricity', gtype: 'electricity', response:[]},
            {device_solar6: 'f3902d10-1d77-11ee-8bc6-01b3484f4adf', name: 'Solar 6', key:'solarElectricity', gtype: 'electricity', response:[]},
            {device_solar7: 'f3591780-1d77-11ee-8bc6-01b3484f4adf', name: 'Solar 7', key:'solarElectricity', gtype: 'electricity', response:[]},
            {device_solar8: 'f36bb520-1d77-11ee-8bc6-01b3484f4adf', name: 'Solar 8', key:'solarElectricity', gtype: 'electricity', response:[]},
            {device_solar9: '3730cf80-1d7c-11ee-8bc6-01b3484f4adf', name: 'Solar 9', key:'solarElectricity', gtype: 'electricity', response:[]},


