descriptor.controllerScript:
self.onInit = function() {
    const $scope=self.ctx.$scope;
    const header={headers:{"Content-Type":"application/json","X-Authorization":"Bearer "+localStorage.getItem('jwt_token')}};
    $scope.topLossActionLogForm=$scope.fb.group({
        date: [moment().format('YYYY-MM-DD')],
        topLoss:$scope.fb.array([]),
        actionLog: $scope.fb.array([])
    });
    let topLossActionLog=[];
    $scope.settings={borderColor:'gray'};
    $scope.days=[];
    $scope.showProgress=true;
    let ddsData=[];
    let enerconData=[];
    for(i=new Date(moment().format('YYYY-01-01 06:00:00')); i<new Date();){
        $scope.days.push({ts: i.getTime(), date: moment(new Date(i)).format('YYYY-MM-DD'), displayedDate: moment(i).format('DD-MMM')});
        i.setDate(i.getDate()+1);
    }
    try{
        enerconData=JSON.parse(self.ctx.settings.jsonSchema);
        if(enerconData){
            resolveAttributeData()
        }
    }catch{

    }
    function resolveAttributeData(){
        self.ctx.attributeService.getEntityAttributes({"entityType": "ASSET","id": "cb3787e0-4631-11ee-8bc6-01b3484f4adf"},'SERVER_SCOPE', ['enerconTargetData', 'enerconYeTargetData', 'topLossActionLog']).subscribe(response=>{
            if(response.length){
                let yeTargetData={};
                let targetData={};
                let yeTarget=response.filter(res=>res.key=='enerconYeTargetData');

                if(yeTarget.length){
                    let targetArray=yeTarget[0].value;
                    targetObj=targetArray.filter(tarr=>tarr.year==moment(new Date()).format('YYYY'));
                    if(targetObj.length){
                        yeTargetData=targetObj[0].target;
                    }
                }

                yeTarget=response.filter(res=>res.key=='enerconTargetData');
                if(yeTarget.length){
                    let targetArray=yeTarget[0].value;
                    targetObj=targetArray.filter(tarr=>tarr.year==moment(new Date()).format('YYYY'));
                    if(targetObj.length){
                        targetData=targetObj[0].target;
                    }
                }
                tlal=response.filter(res=>res.key=='topLossActionLog');
                if(tlal.length){
                    let tlalArray=tlal[0].value;
                    topLossActionLog=tlal[0].value
                    tlalObj=tlalArray.filter(tarr=>tarr.date==moment(new Date()).format('YYYY-MM-DD'));
                    if(tlalObj.length){
                        patchTopLossActionLogForm(tlalObj[0])
                    }else{
                        $scope.addNewFieldToTopLossActionLog('topLoss')
