    function loadProcessedVolume(){
        const startDate=new Date(moment().format('YYYY-01-01 00:00:00')).getTime();
        const req=self.ctx.http.post('/nrdapi/get_production_data', {start_ts:startDate, end_ts: new Date().getTime()}, header);
        req.subscribe(res=>{
            let totalLoadShedding=0;
            let totalProcessedVolume=0;
            if(res.length){
                let pvIndex=getIndexOfKey(enerconData,'key', 'processedVolume');
                let lsIndex=getIndexOfKey(enerconData, 'key', 'loadSheddingTime');
                res.forEach(r=>{
                    enerconData[pvIndex].day_data.push({ts:r.ts, date: moment(new Date(Number(r.ts))).format('YYYY-MM-DD'), value: r.dbl_v});
                    totalProcessedVolume+=Number(r.dbl_v);

                    enerconData[lsIndex].day_data.push({ts:r.ts, date: moment(new Date(Number(r.ts))).format('YYYY-MM-DD'), value: Number((Number(r.loadshedding_ms)/36e5).toFixed(2))});
                    totalLoadShedding+=Number(r.loadshedding_ms);
                });
                enerconData[pvIndex]['ytd']=totalProcessedVolume;
                enerconData[lsIndex]['ytd']=(totalLoadShedding/36e5).toFixed(2);
                fillLine('processedVolume');
                fillLine('loadSheddingTime');
            }
        });
    }
    function commonFunction(startTs, deviceInfoArray, index, length, key){

        if(index<length){
            const req=self.ctx.http.post('/nrdapi/get_daily_consumption',  {start_ts: startTs, end_ts: new Date().getTime(), entity_id: deviceInfoArray[index].id},  header);
            req.subscribe(res=>{
                  deviceInfoArray[index]['response']=res;
                  commonFunction(startTs, deviceInfoArray, index+1, length, key)
            });
        }else{
            if(key=='furnaceOilConsumption'){
                resolveFurnaceOilConsumptionWithPerKG(deviceInfoArray, key)
            }
            if(key=='totalGeneratedSteam'){
                resolveTotalGeneratedSteam(deviceInfoArray, key);
            }if(key=='electricity'){
                resolveOutputElectricity(deviceInfoArray);
            }
            if(key=='electricityConsumption'){
                resolveElectricityConsumption(deviceInfoArray);
            }
            if(key=='dieselConsumption'){
                resolveDieselConsumption(deviceInfoArray);
            }
            if(key=='water'){
                resolveWaterConsumption(deviceInfoArray);
            }
            $scope.data=enerconData;
            self.ctx.detectChanges();
        }
    }
    function resolveFurnaceOilConsumptionWithPerKG(furnaceDevice, key){
            fs=[];
            for(let i=0; i<furnaceDevice.length/2; i++){
                let map1=furnaceDevice[i].response.reduce((map, item)=>{
                    map[item.day]={net_kwh_in: item.net_kwh, net_kwh_rtn:0};
                    return map;
                }, {});
                let map2=furnaceDevice[i+(furnaceDevice.length/2)].response.reduce((map, item)=>{
                    map[item.day]={net_kwh_rtn: item.net_kwh, net_kwh_in:0};
                    return map;
                }, {});
                Object.keys(map1).forEach(key=>{
                    if(map2[key]!=undefined){
                        map1[key].net_kwh_rtn=map2[key].net_kwh_rtn;
                    }
                });

                Object.keys(map2).forEach(key=>{
                    if(map1[key]==undefined){
                        map1[key]={net_kwh_in:0, net_kwh_rtn:map2[key].net_kwh_rtn};
                    }
                });
                const outputArray = Object.keys(map1).map(day => ({
                  day,
                  ...map1[day]
                }));
                fs.push(outputArray);
            }
            fcIndex=getIndexOfKey(enerconData, 'key', 'furnaceOilConsumption');
            let totalFurnaceConsumption=0
            if(fcIndex!==-1){
                fs.forEach(furnaceLine=>{
                    furnaceLine.forEach(furnaceData=>{
                        fcDataIndex=getIndexOfKey(enerconData[fcIndex].day_data, 'ts',new Date(furnaceData.day).getTime());
                        if(fcDataIndex==-1){
                            enerconData[fcIndex].day_data.push({ts: new Date(furnaceData.day).getTime(), date: moment(new Date(furnaceData.day)).format('YYYY-MM-DD'), value: Number((furnaceData.net_kwh_in-furnaceData.net_kwh_rtn).toFixed(2))});
                        }else{
                            enerconData[fcIndex].day_data[fcDataIndex].value+=Number((furnaceData.net_kwh_in-furnaceData.net_kwh_rtn).toFixed(2));
                        }
                        totalFurnaceConsumption+=Number((furnaceData.net_kwh_in-furnaceData.net_kwh_rtn).toFixed(2));
                    });
                });
                enerconData[fcIndex].ytd=totalFurnaceConsumption.toFixed(2);
            }
            const fcPerKgIndex=getIndexOfKey(enerconData, 'key', 'furnaceOilConsumptionPerKG');
            let pvIndex=getIndexOfKey(enerconData, 'key', 'processedVolume');
            if(enerconData[fcIndex].day_data.length){
                enerconData[fcIndex].day_data.forEach(dData=>{
                    pvData=enerconData[pvIndex].day_data.find(pvDayData=>pvDayData.date==dData.date);
                    enerconData[fcPerKgIndex].day_data.push({
                        ts:dData.ts, date: dData.date, value: pvData==undefined?0:pvData.value==0?0:(dData.value/pvData.value).toFixed(2)
                    });
                });
            }
            enerconData[fcPerKgIndex].ytd=totalFurnaceConsumption/Number(enerconData[pvIndex].ytd);
            fillLine('furnaceOilConsumption');
            fillLine('furnaceOilConsumptionPerKG');
    }
    function resolveTotalGeneratedSteam(deviceInfoArray, key){
        let index=getIndexOfKey(enerconData, 'key', key);
        let totalSteam=0;
        deviceInfoArray.forEach(device=>{
            device.response.sort((a,b)=>new Date(a.day)-new Date(b.day));
            consumedIndex=getIndexOfKey(enerconData, 'key', device.name);
            let singleSteamTotal=0;
            device.response.forEach(res=>{
                gIndex=getIndexOfKey(enerconData[index].day_data, 'date', res.day);
                if(gIndex==-1){
                    enerconData[index].day_data.push({ts: new Date(res.day).getTime(), date: res.day, value: Number(Number(res.net_kwh).toFixed(1))});
                }else{
                    enerconData[index].day_data[gIndex].value=Number((enerconData[index].day_data[gIndex].value+Number(res.net_kwh)).toFixed(2));
                }
                totalSteam+=Number(res.net_kwh);
                singleSteamTotal+=Number(res.net_kwh);
                enerconData[consumedIndex].day_data.push({ts: new Date(res.day).getTime(), date: res.day, value: Number(Number(res.net_kwh).toFixed(1))});
            });
            enerconData[consumedIndex].ytd=singleSteamTotal.toFixed();
            fillLine(device.name);
        });
        enerconData[index].ytd=Number(totalSteam.toFixed(2))
        fillLine(key);
        enerconData.forEach((ed,index)=>{
            if(ed.type=='label'){
                ed['colspan']=Object.keys(enerconData[0]).length+enerconData[0]['day_data'].length-1;
                ed['bgColor']='#7030A0';
            }
        });
    }
    function feedWaterTemperatureResolve(startTs, endTs, temperatureDevice, index, length, key){
        self.ctx.http.get('/api/plugins/telemetry/DEVICE/'+temperatureDevice[0].id+'/values/timeseries?keys=ts_temperature&endTs='+endTs+'&startTs='+startTs+'&limit=50000',header).subscribe(response=>{
            if(Object.keys(response).length){
                temperatureDevice[0].response=[...temperatureDevice[0].response, ...response.ts_temperature];
                if(response.ts_temperature.length==50000){
                    feedWaterTemperatureResolve(startTs, response.ts_temperature[response.ts_temperature.length-1].ts, temperatureDevice, index, length, key);
                }else{
                    temperatureDevice[0].response.sort((a,b)=>a.ts-b.ts);
                    let tenIndex=getIndexOfKey(enerconData, 'key', key);
                    for(let i=new Date(moment().format('YYYY-01-01 06:00:00')); i<new Date();){
                        dayTemp=temperatureDevice[0].response.filter(temp=>temp.ts<=i.getTime()&&temp.ts<(i.getTime()+(24*36e5)));
                        totalTemp=0
                        if(dayTemp.length){
                            dayTemp.forEach(dt=>{
                                totalTemp+=Number(dt.value);
                            })
                        }
                        avgTemp=dayTemp.length==0?0:Number((totalTemp/dayTemp.length).toFixed(2))
                        enerconData[tenIndex].day_data.push({ts:i.getTime(), date: moment(i).format('YYYY-MM-DD'), value:avgTemp})
                        i.setDate(i.getDate()+1);
                    }
                    const sum = temperatureDevice[0].response.reduce((accumulator, currentValue) => accumulator + Number(currentValue.value), 0);
                    const average = sum / temperatureDevice[0].response.length;
                    enerconData[tenIndex].ytd=average.toFixed(2);
                    fillLine(key);
                    $scope.showProgress=false;
                    self.ctx.detectChanges();
                }
            }
        });
    }
    function resolveOutputElectricity(devices){
        totalGenerated=0;
        totalSolar=0;
        devices.forEach(device=>{
            enerconIndex=getIndexOfKey(enerconData, 'key', device.key)
            if(device.key=='purchasedElectricity'){
                electricityPerKgIndex=getIndexOfKey(enerconData, 'key', 'electricityConsumtionPerKG');
                pvIndex=getIndexOfKey(enerconData,'key', 'processedVolume');
                let totalwzpdcl=0;
                device.response.forEach(electricityData=>{
                    enerconData[enerconIndex].day_data.push({ts:new Date(electricityData.day).getTime(), date: electricityData.day, value: electricityData.net_kwh.toFixed(2)});
                    pvDataIndex=getIndexOfKey(enerconData[pvIndex].day_data, 'date', electricityData.day);
                    perKg=Number((pvDataIndex==-1?0:enerconData[pvIndex].day_data[pvDataIndex].value==0?0:electricityData.net_kwh/Number(enerconData[pvIndex].day_data[pvDataIndex].value)).toFixed(2));
                    enerconData[electricityPerKgIndex].day_data.push({ts:new Date(electricityData.day).getTime(), date: electricityData.day, value: perKg});
                    totalwzpdcl+=Number(electricityData.net_kwh);
                });
                enerconData[enerconIndex].ytd=totalwzpdcl.toFixed(2);
                enerconData[electricityPerKgIndex].ytd=(totalwzpdcl/Number(enerconData[pvIndex].ytd)).toFixed(2);
            }else if(device.key=='generatedElectricity'){
                device.response.forEach(genData=>{
                    dIndex=getIndexOfKey(enerconData[enerconIndex].day_data, 'date', genData.day);
                    if(dIndex==-1){
                        enerconData[enerconIndex].day_data.push({ts:new Date(genData.day).getTime(), date: genData.day, value: Number(genData.net_kwh.toFixed(2))});
                    }else{
                        enerconData[enerconIndex].day_data[dIndex].value=Number((enerconData[enerconIndex].day_data[dIndex].value+genData.net_kwh).toFixed(2));
                    }
                    totalGenerated+=Number(genData.net_kwh.toFixed(2))
                });
            }else if(device.key=='solarElectricity'){
                device.response.forEach(solar=>{
                    dIndex=getIndexOfKey(enerconData[enerconIndex].day_data, 'date', solar.day);
                    if(dIndex==-1){
                        enerconData[enerconIndex].day_data.push({ts:new Date(solar.day).getTime(), date: solar.day, value: Number(solar.net_kwh.toFixed(2))});
                    }else{
                        enerconData[enerconIndex].day_data[dIndex].value=Number((enerconData[enerconIndex].day_data[dIndex].value+solar.net_kwh).toFixed(2));
                    }
                    totalSolar+=Number(solar.net_kwh)
                });
            }
        });
        gindx=getIndexOfKey(enerconData,'key', 'generatedElectricity');
        enerconData[gindx].ytd=totalGenerated.toFixed(2);
        sindx=getIndexOfKey(enerconData,'key', 'solarElectricity');
        enerconData[sindx].ytd=totalSolar.toFixed(2);
        fillLine('purchasedElectricity'); 
        fillLine('generatedElectricity');
        fillLine('solarElectricity');
        fillLine('electricityConsumtionPerKG');
    }
    function resolveElectricityConsumption(devices){
        devices.forEach(device=>{
            enerconIndex=getIndexOfKey(enerconData, 'key', device.key);
            if(enerconData[enerconIndex].ytd==undefined){
                enerconData[enerconIndex].ytd=0;
            }
            device.response.forEach(consump=>{
                dIndex=getIndexOfKey(enerconData[enerconIndex].day_data, 'date', consump.day);
                if(dIndex==-1){
                    enerconData[enerconIndex].day_data.push({ts:new Date(consump.day).getTime(), date: consump.day, value: Number(consump.net_kwh.toFixed(2))});
                }else{
                    enerconData[enerconIndex].day_data[dIndex].value=Number((enerconData[enerconIndex].day_data[dIndex].value+consump.net_kwh).toFixed(2));
                }
                enerconData[enerconIndex].ytd=Number((enerconData[enerconIndex].ytd+Number(consump.net_kwh.toFixed(2))).toFixed(2));
            });
        });
        devices.forEach(device=>{
            fillLine(device.key);
        });
    }
    function resolveDieselConsumption(devices){
        runtimeList={};
        consumption={};
        ytdRuntime=0;
        totalC=0;
        let tdc=getIndexOfKey(enerconData, 'key', 'dieselConsumption')
        devices.forEach(device=>{
            if(device.type=='runtime'){
                runtimeList[device.key]=[];
                device.response.forEach(rt=>{
                    rtIndex=getIndexOfKey(runtimeList[device.key], 'day', rt.day)
                    if(rtIndex==-1){
                        runtimeList[device.key].push({day: rt.day, value:rt.net_kwh});
                    }else{
                        runtimeList[device.key][rtIndex].value+=rt.net_kwh;
                    }
                    ytdRuntime+=rt.net_kwh;
                });
            }else{
                if(consumption[device.key]==undefined){
                    consumption[device.key]=[];
                }
                device.response.forEach(cons=>{
                    rtIndex=getIndexOfKey(consumption[device.key], 'day', cons.day);
                    if(rtIndex==-1){
                        consumption[device.key].push({day: cons.day, value:(device.type=='in'?cons.net_kwh:-cons.net_kwh)});
                    }else{
                        consumption[device.key][rtIndex].value+=device.type=='in'?cons.net_kwh:-cons.net_kwh;
                    }
                    tdcDataIndex=getIndexOfKey(enerconData[tdc].day_data, 'date', cons.day)
                    if(tdcDataIndex==-1){
                        enerconData[tdc].day_data.push({ts: new Date(cons.day).getTime(), date: cons.day, value: Number((device.type=='in'?cons.net_kwh:-cons.net_kwh).toFixed(2))});
                    }else{
                        enerconData[tdc].day_data[tdcDataIndex].value=Number((enerconData[tdc].day_data[tdcDataIndex].value+(device.type=='in'?cons.net_kwh:-cons.net_kwh)).toFixed(2));
                    }
                    totalC+=device.type=='in'?cons.net_kwh:-cons.net_kwh;
                });
            }
        });
        rtimeIndex=getIndexOfKey(enerconData, 'key', 'generatorRuntime');
        Object.keys(runtimeList).forEach(key=>{
            runtimeList[key].forEach(rtime=>{
                rtIndex=getIndexOfKey(enerconData[rtimeIndex].day_data, 'date', rtime.day);
                if(rtIndex==-1){
                    enerconData[rtimeIndex].day_data.push({ts: new Date(rtime.day).getTime(), date: rtime.day, value:rtime.value});
                }else{
                    enerconData[rtimeIndex].day_data[rtIndex].value=Number((enerconData[rtimeIndex].day_data[rtIndex].value+rtime.value).toFixed(2));
                }
            });
        });
        enerconData[rtimeIndex].day_data.forEach(rtday=>{
            rtday.value=Number((rtday.value/3600).toFixed(2));
        });
        enerconData[rtimeIndex].ytd=Number((ytdRuntime/3600).toFixed(2));
        totalConsumptionPerDevice={};
        Object.keys(consumption).forEach(key=>{
            cIndex=getIndexOfKey(enerconData, 'key', key);
            if(totalConsumptionPerDevice[key]==undefined){
                totalConsumptionPerDevice[key]={cons: 0, ms: 0};
            }
            consumption[key].forEach(c=>{
                rtIndex=getIndexOfKey(runtimeList[key], 'day', c.day);
                let val=rtIndex==-1||runtimeList[key][rtIndex].value==0?0:Number((c.value/(runtimeList[key][rtIndex].value/3600)).toFixed(2));
                enerconData[cIndex].day_data.push({ts: new Date(c.day).getTime(), date: c.day, value: val});
                totalConsumptionPerDevice[key].cons+=c.value;
                totalConsumptionPerDevice[key].ms+=rtIndex==-1?0:runtimeList[key][rtIndex].value;
            });
            fillLine(key);
        });
        Object.keys(totalConsumptionPerDevice).forEach(key=>{
            let index=getIndexOfKey(enerconData,'key', key);
            enerconData[index].ytd=((totalConsumptionPerDevice[key].cons/(totalConsumptionPerDevice[key].ms/3600)).toFixed(2))
        });
        enerconData[tdc].ytd=Number(totalC.toFixed(2));
        fillLine('dieselConsumption'); 
        fillLine('generatorRuntime');
    }

    function resolveWaterConsumption(devices){
        keyList=['waterWithdrawnPerKG', 'waterRecycledPercentage'];
        pvolumeIndex=getIndexOfKey(enerconData, 'key', 'processedVolume');
        withdrawnPerKgIndex=getIndexOfKey(enerconData, 'key', 'waterWithdrawnPerKG');
        devices.forEach(device=>{
            if(keyList.indexOf(device.key)==-1){
                keyList.push(device.key);
            }
            let xindex=getIndexOfKey(enerconData, 'key', device.key);
            device.response.forEach(res=>{
                yindex=getIndexOfKey(enerconData[xindex].day_data, 'date', res.day);
                if(yindex==-1){
                    enerconData[xindex].day_data.push({ts: new Date(res.day).getTime(), date: res.day, value: Number(res.net_kwh.toFixed(2))});
                }else{
                    enerconData[xindex].day_data[yindex].value=Number((enerconData[xindex].day_data[yindex].value+res.net_kwh).toFixed(2));
                }
                enerconData[xindex].ytd=enerconData[xindex].ytd==undefined?res.net_kwh:Number((enerconData[xindex].ytd+res.net_kwh).toFixed(2));

                if(device.key=='waterWithdrawn'){
                    zindex=getIndexOfKey(enerconData[pvolumeIndex].day_data, 'date', res.day)
                    if(zindex!==-1){
                        let xval=isNaN(enerconData[pvolumeIndex].day_data[zindex].value) || enerconData[pvolumeIndex].day_data[zindex].value==0?0:Number((res.net_kwh/enerconData[pvolumeIndex].day_data[zindex].value).toFixed(2));
                        enerconData[withdrawnPerKgIndex].day_data.push({ts: new Date(res.day).getTime(), date: res.day, value:xval });
                    }
                }
            });
        });
        rycleIndex=getIndexOfKey(enerconData, 'key', "waterRecycled");
        widthdrawnIndex=getIndexOfKey(enerconData, 'key', "waterWithdrawn");
        percentIndex=getIndexOfKey(enerconData, 'key', 'waterRecycledPercentage');
        let totalWithdrawn=0;
        let totalrecycled=0;
        enerconData[rycleIndex].day_data.forEach(rData=>{
            const ddIndex=getIndexOfKey(enerconData[widthdrawnIndex].day_data, 'date', rData.date)
            ttval=ddIndex==-1?0:Number(((rData.value/(rData.value+enerconData[widthdrawnIndex].day_data[ddIndex].value))*100).toFixed(2));
            enerconData[percentIndex].day_data.push({ts: rData.ts, date: rData.date, value: ttval});
            totalWithdrawn+=ddIndex==-1?0:enerconData[widthdrawnIndex].day_data[ddIndex].value;
            totalrecycled+=rData.value;
        });
        enerconData[percentIndex].ytd=Number(((totalrecycled/(totalrecycled+totalWithdrawn))*100).toFixed(2))
        enerconData[withdrawnPerKgIndex].ytd=Number((totalWithdrawn/Number(enerconData[pvolumeIndex].ytd)).toFixed(5))
        keyList.forEach(key=>{
            fillLine(key);
        })

    }
    function getIndexOfKey(arr,key, val){
        return arr.findIndex(eData=>eData[key]==val);
    }
    function fillLine(key){
        index=getIndexOfKey(enerconData, 'key', key);
        $scope.days.forEach(day=>{
            if(getIndexOfKey(enerconData[index].day_data, 'date', day.date)==-1){
                enerconData[index].day_data.push({ts: day.ts, date:day.date, value:'-'});
            }
        });
        enerconData[index].day_data.sort((a,b)=>a.ts-b.ts);
        $scope.data=enerconData;
        self.ctx.detectChanges();
        self.onResize();
    }

    // let data=[];
    // for(let j=0; j<100; j++){
    //     let total=0;
    //     let entry={kpi: Math.random()*10000, unit: 'm3', target: '654'};
    //     let dayData=[];
    //     for(i=new Date(moment('2023-08-01')); i<new Date();){
