    //         let obj={'date': moment(i).format('DD-MMM')};
    //         obj['value']=x.toFixed(2);
    //         dayData.push(obj);
    //         total+=x;
    //         i.setDate(i.getDate()+1);
    //     }
    //     entry['day_data']=dayData;
    //     entry['ye_target']=464;
    //     entry['ytd']=total.toFixed(2);
    //     entry['type']='data'
    //     data.push(entry);
    // }
    // let colspan=Object.keys(data[0]).length+data[0]['day_data'].length-1;
    // data[1]={};
    // data[1]['type']='label';
    // data[1]['label']='Output Measure (Furnace Oil)';
    // data[1]['bgColor']='#7030A0';
    // data[1]['colspan']=colspan;
    // $scope.data=data;
    self.onResize();
}

self.onDataUpdated = function() {

}

self.onResize = function() {
    const $scope=self.ctx.$scope;
    $scope.height=self.ctx.height;
    $scope.width=self.ctx.width;
    $scope.isMobile=self.ctx.isMobile;
    self.ctx.detectChanges();

    $(document).ready(function(){
        function scrollRight() {
        let scrollContainer = $("#enerconDDsContainer");
        scrollContainer.animate({ scrollLeft: scrollContainer[0].scrollWidth }, 0);
    }
    setTimeout(scrollRight, 1000);
    })
}

self.onDestroy = function() {

}

