self.onInit = function() {
    $(document).ready(function(){
      self.onResize();
    });
    const { $scope } = self.ctx
    $scope.searchVal='';
    $scope.displayedColumns=['select'];
    $scope.bgColor=self.ctx.widgetConfig.backgroundColor;
    $scope.color=self.ctx.widgetConfig.color;
    $scope.settings=self.ctx.settings;
    $scope.formHeight=self.ctx.isMobile?self.ctx.height/2:self.ctx.height;
    $scope.height=self.ctx.isMobile?self.ctx.height/2:self.ctx.height-65;
    const tablePagination = {
        pageIndex: 0,
        pageSize: 20,
        pageSizeOptions: [20, 50, 100],
        handler: (pagination) => {
            const { pageIndex, pageSize } = pagination;
            if (pageSize !== undefined) {
                tablePagination.pageSize = pageSize;
            }
            if (pageIndex !== undefined) {
                tablePagination.pageIndex = pageIndex;
            }
        },
        getFrom: () => {
            const { pageIndex, pageSize } = tablePagination;
            return pageIndex * pageSize;
        },
        getTo: () => {
            const { pageIndex, pageSize } = tablePagination;
            return pageIndex * pageSize + pageSize;
        }
    };
    $scope.tablePagination=tablePagination;
    $scope.styleOfCheckbox=function(row){
        $('.tb-default mat-cell.checkboxcell .mat-checkbox-frame').css('border', '1px solid '+self.ctx.widgetConfig.color);
        return $scope.reportDevices.indexOf(row)>-1;
    }
    $scope.actn={};
    let dvd = self.ctx.actionsApi.getActionDescriptors('ReportViewBtn');
    self.ctx.$scope.dvd = dvd;
    let defData=self.ctx.defaultSubscription.data
    let allData=[];
    $scope.allChecked=false;
    $scope.reportDevices=[];
    if(defData){
        defData.forEach(dt=>{
            allData.push({'name': dt.datasource.entityLabel!=''?dt.datasource.entityLabel:dt.datasource.entityName, 'id': dt.datasource.entityId});
        });
        allData.sort((a,b)=>{return a.name.localeCompare(b.name)});
        $scope.allData=[...allData];
        self.ctx.detectChanges();
    }
    buildForm(new Date());
    $scope.reportTypes=['Hourly', 'Daily', 'Monthly', 'Yearly', 'Interval']
    $scope.today=new Date();
    let reportDevices = [];

    $scope.searchData=function(e, d, dvc){
        let fg=$scope.fb.array([]);
        if(reportDevices.length>0){
            reportDevices.forEach(rd=>{
                fg.push($scope.fb.group({
                    name: rd.name,
                    id: rd.id
                }));
            });
            $scope.form.setControl('devices', fg);
        }else{
            self.ctx.showToast('warn', 'No device is selected', 2000, 'top', 'start',  self.ctx.$scope.toastTargetId);
            return;
        }
        if($scope.form.valid){
            ss=moment(moment(new Date($scope.form.value.startDate)).format('YYYY-MM-DD')+' '+$scope.form.value.startTime).unix();
            ee=moment(moment(new Date($scope.form.value.endDate)).format('YYYY-MM-DD')+' '+$scope.form.value.endTime).unix();
            if(ss>ee){
                self.ctx.showToast('error', 'Start datetime is greater than end datetime', 2000, 'top', 'start',  self.ctx.$scope.toastTargetId);
            }else{
                localStorage.setItem('report_data_format', JSON.stringify($scope.form.value));
                self.ctx.actionsApi.handleWidgetAction(e, d, dvc.id, dvc.name);
            }
        }else{
            if($scope.form.value.reportType=='Status'){
                localStorage.setItem('report_data_format', JSON.stringify($scope.form.value));
                self.ctx.actionsApi.handleWidgetAction(e, d, dvc.id, dvc.name);
            }else if($scope.form.value.reportType=='Yearly'||$scope.form.value.reportType=='Monthly'){
                if($scope.form.value.startDate==''||$scope.form.value.endDate==''){
                    self.ctx.showToast('warn', 'Invalid form input', 2000, 'top', 'start',  self.ctx.$scope.toastTargetId);
                }else{
                    localStorage.setItem('report_data_format', JSON.stringify($scope.form.value));
                    self.ctx.actionsApi.handleWidgetAction(e, d, dvc.id, dvc.name);
                }
            }else{
                self.ctx.showToast('warn', 'Invalid form input', 2000, 'top', 'start',  self.ctx.$scope.toastTargetId);
            }
        }

    }
    $scope.applyFilter=function(searchVal){
        $scope.allData=[...allData];
        $scope.allData = $scope.allData.filter(ele=>{
            if(ele.name.toLowerCase().search(searchVal)!=-1){
                return ele;
            }
        });
        self.ctx.detectChanges();
    }
    $scope.f=function(event){
        $scope.form.reset();
        buildForm(event.value=='Hourly'||event.value=='Daily'||event.value=='Interval'?new Date():'');
        $scope.form.controls['reportType'].setValue(event.value);
        self.ctx.detectChanges();
        self.onResize();
    }
    $scope.openDatePicker=function(dp) {
        dp.open();
    }
    self.ctx.$scope.selectStartDate=function(eventData, dp, format) {
        $scope.form.controls['startDate'].setValue(moment(eventData).format(format));
        $("#startDate").val(moment(eventData).format(format));
        dp.close();
    }
    self.ctx.$scope.selectEndDate=function(eventData, dp, format) {
        $scope.form.controls['endDate'].setValue(moment(eventData).format(format));
        $("#endDate").val(moment(eventData).format(format));
        dp.close();
    }
    function buildForm(endt){
        today=new Date();
        $scope.form=$scope.fb.group({
            devices: $scope.fb.array([]),
            reportType: ['', $scope.validators.required],
            startDate: ['', $scope.validators.required],
            startTime: ['00:00', $scope.validators.required],
            endDate: [endt, $scope.validators.required],
            endTime: [today.getHours()+':'+(today.getMinutes()>9?today.getMinutes():'0'+today.getMinutes()), $scope.validators.required]
        });
    }
    $scope.removeDevice=function(element){
        reportDevices=reportDevices.filter(d=>d!=element);
        delete $scope.reportDevices;
        $scope.reportDevices=reportDevices;
        self.ctx.detectChanges();
    }

    $scope.selectAllDevice=function(event){
        if(event.checked){
            reportDevices=[...$scope.allData];
        }else{
            reportDevices=[];
            if(reportDevices.length==0){
                $scope.form.reset();
                today=new Date();
                $scope.form.patchValue({
                    startTime:'00:00',
                    endDate: new Date(),
                    endTime: today.getHours()+':'+(today.getMinutes()>9?today.getMinutes():'0'+today.getMinutes())
                });
            }
        }
        $scope.reportDevices=[...reportDevices];
        $scope.allChecked=_.isEqual($scope.allData,reportDevices);
        $scope.form.patchValue({
            'reportType': reportDevices.length>1?'Interval':''
        });
        self.ctx.detectChanges();
        self.onResize();
    }
    $scope.selectIndividualDevice=function(event, data){
        if(event.checked){
            reportDevices.push(data);
        }else{
            reportDevices=reportDevices.filter(rd=>rd!=data);
            if(reportDevices.length==0){
                $scope.form.reset();
                today=new Date();
                $scope.form.patchValue({
                    startTime:'00:00',
                    endDate: new Date(),
                    endTime: today.getHours()+':'+(today.getMinutes()>9?today.getMinutes():'0'+today.getMinutes())
                });
            }
        }
        reportDevices.sort((a,b)=>{return a.name.localeCompare(b.name)});
        $scope.allChecked=_.isEqual($scope.allData,reportDevices);
        $scope.reportDevices=[...reportDevices];
        $scope.form.patchValue({
                'reportType': reportDevices.length>1?'Interval':'Daily'
        });
        self.ctx.detectChanges();
        self.onResize();
    }
    $scope.isDeviceChecked=function(data){
        return reportDevices.indexOf(data)>-1;
    }

}
self.onDataUpdated=function(){
    self.onResize();
}
self.actionSources = function() {
     return {
         'ReportViewBtn': {name: 'Report view action',multiple: true}
     };
 }
 self.onResize=function(){
    $scope=self.ctx.$scope;
    $scope.settings=self.ctx.settings;
    $scope.height=self.ctx.isMobile?self.ctx.height/2:self.ctx.height-65;
    $scope.formHeight=self.ctx.isMobile?self.ctx.height/2:self.ctx.height;

    $('.tb-default mat-cell.checkboxcell .mat-checkbox-frame').css('border', '1px solid '+self.ctx.widgetConfig.color);
    // $('.tb-default mat-header-cell.headercheckboxcell .mat-checkbox-frame').css('border', '1px solid '+self.ctx.settings.tableHeaderTxtColor);
    $('mat-paginator.report-form-paginator .mat-select-value-text').css('color', self.ctx.widgetConfig.color);
    $('.tb-default mat-paginator.report-form-paginator .mat-select-arrow, .tb-default .satl-report-ff .mat-select-arrow, .tb-default .satl-report-ff button').css('color', self.ctx.widgetConfig.color);
    $('.tb-default mat-paginator.report-form-paginator .mat-form-field-appearance-legacy .mat-form-field-underline').css('background-color', self.ctx.widgetConfig.color);
    $('.tb-default .satl-report-ff .mat-form-field-appearance-outline .mat-form-field-outline').css('color',self.ctx.widgetConfig.color);
    $('.tb-default #mat-report-header-search-box .mat-form-field-appearance-outline .mat-form-field-outline').css('color',self.ctx.settings.tableHeaderTxtColor);
    $(document).ready(function() {
        $('mat-paginator.report-form-paginator .mat-select-value-text').css('color', self.ctx.widgetConfig.color);
        $('mat-paginator.report-form-paginator .tb-default .mat-select-arrow').css('color', self.ctx.widgetConfig.color);
        $('.tb-default mat-form-field.satl-report-ff .mat-form-field-appearance-legacy .mat-form-field-underline').css('background-color', self.ctx.widgetConfig.color);
        $('.mat-form-field-appearance-outline .mat-form-field-outline').css('color',self.ctx.widgetConfig.color);
        $('.tb-default #mat-report-header-search-box .mat-form-field-appearance-outline .mat-form-field-outline').css('color',self.ctx.settings.tableHeaderTxtColor);
        // $('.tb-default #mat-report-header-search-box .mat-input-element').css('caret-color',self.ctx.settings.tableHeaderTxtColor);

    });
 }
