    border-radius: 5px;
    border: 1px solid #fff;
    /*color: #FFF;*/
    /*background-color: #19b159;*/
    /*background-color: #bf0004;*/
    /*background-color: #444444;*/
    /*background-color: #111;*/
}
.spacer {
  flex: 1 1 auto;
}

.node-text {
  display: inline-block;
  width: 400px;
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
}

.innerWindow{
    width: calc(100% - 10px);
    margin-left: 5px;
    height: 60px;
    background-color: #323232;
    color: #FFF;
    display: flex;
    flex-direction: row;
