}
.innerWindow > span .cont {
    font-size: 15px;
}
.dynamicContainer{
    display: flex;
    flex-wrap: wrap;
    padding: 3px;
    width: 100%;
}
.dynamicContainer > div{
    background-color:white;
    color:black;
    margin:2px;
    width:calc(50% - 4px);
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    padding: 10px;
