descriptor.controllerScript:
self.onInit = function() {
    let settings = self.ctx.settings;
    self.ctx.$scope.settings = settings;
    let dvd = self.ctx.actionsApi.getActionDescriptors('detailsViewButton');
    self.ctx.$scope.dvd = dvd;

    
    self.ctx.$scope.tfr = function(d,k,tt,ft,dk = null){
        let str = '----';
        if(d.data[k] && dk===null){
            let dd = d.data[k].value;
            str = typeof dd==='string'?dd==='true'?tt:ft:dd?tt:ft;
        }else if(d.data[k] && d.data[dk]){
            let dkd = d.data[dk].value;
            let tf = typeof dkd==='string'?dkd==='true'?true:false:dkd?true:false;
            if (tf) {
                let dd = d.data[k].value;
                str = typeof dd==='string'?dd==='true'?tt:ft:dd?tt:ft;
            }
        }
        return str;
    }
    
    self.ctx.$scope.bgcgc = function(d,kc,kh,ks){
        let ocv = d.data[kc]?d.data[kc].value:true;
        let ohv = d.data[kh]?d.data[kh].value:true;
        let osv = d.data[ks]?d.data[ks].value:true;
        let conn = typeof ocv==='string'?ocv==='true'?true:false:ocv?true:false;
        let hlth = typeof ohv==='string'?ohv==='true'?true:false:ohv?true:false;
        let stat = typeof osv==='string'?osv==='true'?true:false:osv?true:false;
        
        if(conn){
            if(hlth){
                if(stat){
                    return settings.bgHealthTrueStatTrueColor;
                }else{
                    return settings.bgHealthTrueStatFalseColor;
                }
            }else{
                if(stat){
                    return settings.bgHealthFalseStatTrueColor;
                }else{
                    return settings.bgHealthFalseStatFalseColor;
                }
            }
        }else{
            return settings.bgConnFalseColor;
        }
        
        return '#000000';
    }
    
    self.ctx.$scope.gvbk = function(d,k,dk){
        let str = '----';
        
        if(d.data[k] && d.data[dk]){
            
            let dkd = d.data[dk].value;
            let tf = typeof dkd==='string'?dkd==='true'?true:false:dkd?true:false;
            if (tf) {
                let obj = d.data[k];
                str = typeof obj.value=='number'?obj.value.toFixed(obj.decimals):obj.value;
                str += obj.units?obj.units:'';
            }
            
        }
        return str;
    }
    
    self.ctx.$scope.detailButtonAction = function(e,d,dp){
        self.ctx.actionsApi.handleWidgetAction(e, d[0], dp.entityId, dp.entityName, {test:"test"}, dp.entityLabel);
    }
}

self.onDataUpdated = function() {
    let brfvRemapLatestValue = function(data) {
        //working for latest data
        let newData = {};
        let index = 0;
        for(index = 0; index < data.length; index++) {
            let iData = data[index];
            if (iData && iData.data && iData.data.length > 0) {
                let eid = iData.datasource.entityName;
                if(typeof newData[eid] === 'undefined') newData[eid] = {};
                newData[eid]['entityId'] = iData.datasource.entity.id;
                newData[eid]['entityName'] = iData.datasource.entityName;
                newData[eid]['entityLabel'] = iData.datasource.entityLabel;
                if(typeof newData[eid]['data'] === 'undefined') newData[eid]['data'] = {};
                newData[eid]['data'][iData.dataKey.name] = {
                    value: iData.data[0][1],
                    timestamp: iData.data[0][0],
                    label: iData.dataKey.label,
                    color: iData.dataKey.color,
                    decimals: iData.dataKey.decimals,
                    units: iData.dataKey.units
                }
            }
        }
        return Object.keys(newData).map((key) => newData[key]);
    }
    
    
    let settings = self.ctx.settings;
    let updata = brfvRemapLatestValue(self.ctx.data);
    
    if(updata[0] || updata.lenght ) {
        self.ctx.$scope.devices = updata;
        self.ctx.detectChanges();
    }
}

self.onResize = function() {
    
}

 self.actionSources = function() {
     return {
         'detailsViewButton': {name: 'Details View Button',multiple: false}
     };
 }

self.onDestroy = function() {
}

