descriptor.controllerScript:
self.onInit = function() {

    self.ctx.$scope.updateCounter = 0;
    self.ctx.$scope.updateTW = function(){
        let singleDayMs = 86400000;
        let m = moment(moment().format('YYYY-MM-DD 00:00'));
        m.add(1, 'day');
        ct = m.unix()*1000;
        m.subtract(1, 'years');
        pt = m.unix()*1000;

        self.ctx.timewindowFunctions.onUpdateTimewindow(pt, ct, singleDayMs);
    }

    self.ctx.flot = new TbFlot(self.ctx, 'bar');
}

self.onDataUpdated = function() {

    if(self.ctx.$scope.updateCounter++ == 2){ 
        self.ctx.$scope.updateTW();
    }

    if(self.ctx.data.length>0){

        // to set the window size
        self.ctx.defaultSubscription.timeWindow.interval = self.ctx.defaultSubscription.subscriptionTimewindow.aggregation.timeWindow/12;

        let cd = new Date();
        let months = ['JAN','FEB','MAR','APR', 'MAY', 'JUN', 'JUL', 'AUG', 'SEP', 'OCT', 'NOV', 'DEC'];
        let monthsTs = [];
        for(let month of months){
            monthsTs.push((new Date(month + " 01 " + (cd.getFullYear()-1))).getTime());
        }
        for(let month of months){
            monthsTs.push((new Date(month + " 01 " + (cd.getFullYear()))).getTime());
        }
        monthsTs.push((new Date("JAN 01 " + (cd.getFullYear()+1))).getTime());

        let oldDataSets = self.ctx.defaultSubscription.data[0].data;
        let newDataSets = [];

        for(let i=0; i< 24; i++) {
            let cdata = 0;
            for(let oldDataSet of oldDataSets){
                if(monthsTs[i] <= oldDataSet[0] && monthsTs[i+1] > oldDataSet[0]){
                    cdata += oldDataSet[1];
                }
            }
            if (cdata) newDataSets.push([monthsTs[i], cdata]);
        }
        self.ctx.defaultSubscription.data[0].data = newDataSets;
    }
    self.ctx.flot.update();
}

self.onResize = function() {
    self.ctx.flot.resize();
}

self.onEditModeChanged = function() {
    self.ctx.flot.checkMouseEvents();
}

self.getSettingsSchema = function() {
    return TbFlot.settingsSchema('bar');
}

self.getDataKeySettingsSchema = function() {
    return TbFlot.datakeySettingsSchema(false, 'bar');
}

self.typeParameters = function() {
    return {
        maxDatasources: 1,
        maxDataKeys: 1,
        singleEntity: true
    };
};

self.onDestroy = function() {
    self.ctx.flot.destroy();
}

