descriptor.controllerScript:
self.onInit = function() {
    deviceData=self.ctx.data;
    let $scope=self.ctx.$scope;
    $scope.settings=self.ctx.settings;
    $scope.globalTxtColor=self.ctx.widgetConfig.color;
    $scope.globalBgColor=self.ctx.widgetConfig.backgroundColor;
    $scope.step = 0;
    $scope.days=['Saturday','Sunday','Monday','Tuesday','Wednesday','Thursday','Friday']
    $scope.form=$scope.fb.group({
        'default':$scope.fb.array([]),
        'weekDays':$scope.fb.array([]),
    });
    let key='tariff';
    if(self.ctx.data.length>0){
        key=self.ctx.data[0].dataKey.name;
    }
    if(deviceData[0].data.length>0&&deviceData[0].data[0][1]!=""){
        try{
            jsonData=JSON.parse(deviceData[0].data[0][1])
            $scope.form.patchValue({});
            if(jsonData['default']!=undefined){
                let defaultArray=$scope.fb.array([]);
                jsonData.default.forEach(defaultData=>{
                    defaultArray.push($scope.fb.group({
                        'title':[defaultData.title,$scope.validators.required],
                        'startTime':[defaultData.startTime],
                        'endTime':[defaultData.endTime],
                        'rate':[defaultData.rate],
                        'rdntRate':[defaultData.rdntRate]
                    }));
                });
                $scope.form.setControl('default', defaultArray);
                
            }else{
                createDefault('00:00');
            }
            if(jsonData['weekDays']!=undefined){
                let weekDayArray=$scope.fb.array([]);
                keys=Object.keys(jsonData.weekDays);
                keys.forEach(key=>{
                    weekDay=jsonData.weekDays[key];
                    tarrifData=$scope.fb.array([]);
                    weekDay.forEach(day=>{
                        tarrifData.push($scope.fb.group({
                            'title':[day.title,$scope.validators.required],
                            'startTime':[day.startTime],
                            'endTime':[day.endTime],
                            'rate':[day.rate],
                            'rdntRate':[day.rdntRate]
                        }));
                    });
                    weekDayArray.push($scope.fb.group({
                        'day':[key,$scope.validators.required],
                        'tarrifData':tarrifData
                    }));
                    
                });
                $scope.form.setControl('weekDays', weekDayArray);
            }
        }catch{
            createDefault('00:00');
        }
    }else{
        createDefault('00:00');
    }
    function createDefault(start){
        $scope.form.get('default').push($scope.fb.group({
            'title':['', $scope.validators.required],
            'startTime':[start, $scope.validators.required],
            'endTime':['23:59', $scope.validators.required],
            'rate':[null, $scope.validators.required],
            'rdntRate':[null],
        }));
    }
    $scope.addDefaultToForm=(time)=>{
        createDefault(time);
    }
    $scope.removeDefault=(index)=>{
        $scope.form.get('default').removeAt(index);
    }
    function createWeekDays(){
        $scope.form.get('weekDays').push($scope.fb.group({
            'day':['', $scope.validators.required],
            'tarrifData':$scope.fb.array([])
        }));
    }
    $scope.addWeekDaysToForm=(time)=>{
        createWeekDays();
        createWeekTarrif($scope.form.get('weekDays').value.length-1, '00:00')
    }
    $scope.removeWeekDays=(index)=>{
        $scope.form.get('weekDays').removeAt(index);
    }
    function createWeekTarrif(id, time){
        $scope.form.get('weekDays').at(id).get('tarrifData').push($scope.fb.group({
            'title':['', $scope.validators.required],
            'startTime':[time, $scope.validators.required],
            'endTime':['23:59', $scope.validators.required],
            'rate':[null, $scope.validators.required],
            'rdntRate':[null]
        }));
    }
    $scope.addWeekTarrifToWeek=(index, time)=>{
        createWeekTarrif(index, time);
    }
    $scope.removeWeekDaysTarrif=(index, i)=>{
        $scope.form.get('weekDays').at(index).get('tarrifData').removeAt(i);
    }
    $scope.saveTarrifs=()=>{
        if($scope.form.valid){
            data={}
            data['default']=[];
            formData=$scope.form.value;
            data['default']=formData['default'];
            data['weekDays']={};
            validation=checkDefaultValidation(formData);
            if (!validation.hasError){
                validation=checkWeekDaysValidation(formData.weekDays);
            }
            if(formData.weekDays.length>0){
                formData.weekDays.forEach(weekDay=>{
                    data['weekDays'][weekDay.day]=weekDay.tarrifData;
                });
            }
            if(validation.hasError){
                self.ctx.showToast('error', validation.errorMessage, 2000, 'top', 'start',  self.ctx.$scope.toastTargetId);
            }else{
                self.ctx.attributeService.saveEntityAttributes(deviceData[0].datasource.entity.id, 'SERVER_SCOPE', [{key:key, value:data}]).subscribe(response=>{
                    self.ctx.showToast('success', 'Tariff plan saved successfully', 2000, 'top', 'start',  self.ctx.$scope.toastTargetId);
                })
            }
            
        }else{
            self.ctx.showToast('error', 'Invalid form input', 2000, 'top', 'start',  self.ctx.$scope.toastTargetId);
        }
    }
    $scope.selectedDay=($event, index)=>{
    }
    function hourToMinute(hour){
        return (Number(hour.split(':')[0])*60)+Number(hour.split(':')[1]);
    }
    function checkDefaultValidation(formData){
        hasError=false;
        errorMessage='';
        total=0;
        formData.default.forEach(defaultData=>{
            start=hourToMinute(defaultData.startTime)
            end=hourToMinute(defaultData.endTime)
            if(start>=end){
                hasError=true;
                errorMessage='Default start and end time greater or overlapped';
            }
            total+=end-start
        });
        if(total!=1439){
            hasError=true;
            errorMessage='Default tarrif must cover 24 hours';
        }
        return {'hasError':hasError, 'errorMessage':errorMessage};
    }
    function checkWeekDaysValidation(weekDays){
        hasError=false;
        errorMessage='';
        if(weekDays.length){
            dayList=[];
            weekDays.forEach(weekDay=>{
                if(dayList.indexOf(weekDay.day)==-1){
                    dayList.push(weekDay.day);
                }else{
                    hasError=true;
                    errorMessage='Duplicate day selected';
                }
                total=0;
                weekDay.tarrifData.forEach(tarrifData=>{
                    start=hourToMinute(tarrifData.startTime)
                    end=hourToMinute(tarrifData.endTime)
                    if(start>=end){
                        hasError=true;
                        errorMessage=weekDay.day+' tarrif data start and end time greater or overlapped';
                    }
                    total+=end-start
                });
                if(total!=1439){
                    hasError=true;
                    errorMessage=weekDay.day+' tarrif must cover 24 hours';
                }
            });
        }
        return {'hasError':hasError, 'errorMessage':errorMessage};
    }
    $scope.setStep=function(index){
        $scope.step = index;
    }
}
self.typeParameters = function() {
    return {
        maxDatasources: 1,
        maxDataKeys: 1,
        singleEntity: true
    };
};

