  background: #CFD8DC;
}
.satl-widget-report::-webkit-scrollbar-thumb {
  background-color: #90A4AE ;
  border-radius: 6px;
  border: 3px solid #CFD8DC;
}
button.mat-button-disabled{
    opacity: 0.35;
}
button.mat-button-disabled:hover {
    cursor: not-allowed;
}
.clrWhite{
    color: white!important;
}

.time-input-field::-webkit-calendar-picker-indicator {
   filter: invert(1) sepia(1) saturate(1) hue-rotate(175deg);
}
