descriptor.controllerScript:
self.onInit = function() {
    if(self.ctx.settings.darkMode){
        $('.tb-default .mat-form-field-appearance-outline .mat-form-field-outline').addClass('clrWhite');
        $('.tb-default .mat-form-field-label, .tb-default .mat-hint').addClass('clrWhite');
        $('input[type="time"]').addClass('time-input-field');
    }
    $('.tb-default .mat-select-value').css('color','inherit');
    $('.tb-default .mat-select-arrow').css('color','inherit');
    self.ctx.$scope.wSettings=self.ctx.widgetConfig;
    self.ctx.$scope.settings=self.ctx.settings;
    self.ctx.$scope.height=self.ctx.height;
    self.ctx.detectChanges();
    today=new Date();
    toastService=self.ctx.$injector.get(self.ctx.servicesMap.get('utils'));
    self.ctx.$scope.formData={
    }
    self.ctx.$scope.device=[];
    baseUrl='/';
    self.ctx.$scope.progressPercent=0;
    self.ctx.$scope.showProgress=false;
    latestData=self.ctx.defaultSubscription.data;
    isOpend=false;
    self.ctx.$scope.showMultiReportFields=false;
    self.ctx.$scope.reportTypes=['Hourly', 'Daily','Monthly','Yearly', 'History', 'Status'];
    selectedDevice={};
    selectedType='Hourly';
    dataSource=[];
    self.ctx.data.forEach(d=>{
        dataSource.push({
            "name":d.datasource.entityName,
            "id":d.datasource.entity.id.id
        });
    });

    let dataArr = dataSource.map(item=>{
        return [item.name,item]
    }); // creates array of array
    let maparr = new Map(dataArr); // create key value pair from array of array

    devices = [...maparr.values()];//converting back to array from mapobject
    self.ctx.$scope.devices=devices;

    self.ctx.$scope.today=new Date();

    self.ctx.$scope.tableData=[];
    self.ctx.$scope.showTable=false;
    deviceData=self.ctx.defaultSubscription.data[0];
    http=self.ctx.http;
    let token = localStorage.getItem('jwt_token');


    /**UI related functions start here**/
    self.ctx.$scope.selectedValue=function($event){
        if(selectedDevice!==$event.value){
            self.ctx.$scope.formData={};
        }
        self.ctx.$scope.selectedDevices=$event.value;
        selectedDevice=$event.value;
        if(selectedDevice.length>1){
            self.ctx.$scope.showMultiReportFields=true;
        }else{
            self.ctx.$scope.showMultiReportFields=false;
        }
        self.ctx.detectChanges();
        if(self.ctx.settings.darkMode){
            $('.tb-default .mat-form-field-appearance-outline .mat-form-field-outline').addClass('clrWhite');
        }

    }
    self.ctx.$scope.changeType=function($event){
        self.ctx.$scope.formData.startDate=undefined;
        self.ctx.detectChanges();
        $('.tb-default .mat-select-value').css('color','inherit');
        $('.tb-default .mat-select-arrow').css('color','inherit');
        $('.tb-default .mat-datepicker-content .mat-calendar-next-button, .tb-default .mat-datepicker-content .mat-calendar-previous-button, .tb-default .mat-datepicker-toggle').css('color','inherit');
        now=today.getHours()+':'+(today.getMinutes()>9?today.getMinutes():'0'+today.getMinutes());
        self.ctx.$scope.formData.startTime='00:00';
        self.ctx.$scope.formData.endTime=now;
        if($event.value=='Monthly'){
            self.ctx.$scope.formData.endDate=moment().format('YYYY-MM');
            $("#endDate").val(moment().format('YYYY-MM'));
        }else if($event.value=='Yearly'){
            self.ctx.$scope.formData.endDate=moment().format('YYYY');
            $("#endDate").val(moment().format('YYYY'));
        }else{
            self.ctx.$scope.formData.endDate=new Date();
        }
        selectedType=$event.value;
        self.ctx.detectChanges();
        if(self.ctx.settings.darkMode){
            $('.tb-default .mat-form-field-appearance-outline .mat-form-field-outline').addClass('clrWhite');
            $('.tb-default .mat-form-field-label, .tb-default .mat-hint').addClass('clrWhite');
            $('input[type="time"]').addClass('time-input-field');
        }
        // $('mat-form-field.mat-form-field-invalid.ng-invalid div.mat-form-field-wrapper div.mat-form-field-flex div.mat-form-field-outline').removeClass('clrWhite');
    }
  self.ctx.$scope.openDatePicker=function(dp) {
    dp.open();
  }
  self.ctx.$scope.selectStartMonth=function(eventData, dp) {
      self.ctx.$scope.formData.startDate=moment(eventData).format('YYYY-MM');
    $("#startDate").val(moment(eventData).format('YYYY-MM'));
    dp.close();    
  }

  self.ctx.$scope.selectEndMonth=function(eventData, dp) {
      self.ctx.$scope.formData.endDate=moment(eventData).format('YYYY-MM');
    $("#endDate").val(moment(eventData).format('YYYY-MM'));
    dp.close();    
  }

  self.ctx.$scope.selectStartYear=function(eventData, dp) {
      self.ctx.$scope.formData.startDate=moment(eventData).format('YYYY');
    $("#startDate").val(moment(eventData).format('YYYY'));
    dp.close();    
  }

  self.ctx.$scope.selectEndYear=function(eventData, dp) {
      self.ctx.$scope.formData.endDate=moment(eventData).format('YYYY');
    $("#endDate").val(moment(eventData).format('YYYY'));
    dp.close();    
  }


  self.ctx.$scope.formatTime=function(time){
      splited=time.split(/[:]/);
      hour=+splited[0];
      if(hour>12){
          return hour-12+':'+splited[1]+' PM';
      }else{
          return time+' AM';
      }
  }
  /**UI related functions end here**/
  self.ctx.$scope.searchData=function(){
        if(selectedType.toLowerCase()=='status'){
            handleStatusData();
        }else{
            const timeData=getTimeData();
            if(timeData.startUnix>timeData.endUnix){
                self.ctx.showToast('warn', 'Start date time must be less than end date time', 2000, 'top', 'start',  self.ctx.$scope.toastTargetId);
                return null;
            }
            const requestParams=getFormattedSettingsData();
            const tableInfo=generateTableData();
            self.ctx.$scope.tableMeta=tableInfo.tableMeta;
            self.ctx.$scope.displayedColumns=tableInfo.displayedColumns;
            self.ctx.$scope.showProgress=true;
            self.ctx.$scope.showTable=false;
            if(requestParams.length>0){
                if(selectedDevice.length>1){
                    multideviceParams=[];
                    selectedDevice.forEach(device=>{
                        requestParams.forEach(param=>{
                            multideviceParams.push({'device':device, 'param':param});
                        })
                    });
                    handleMultiDeviceHttpRequests(multideviceParams, multideviceParams.length-1, timeData, [], 0, multideviceParams.length);
                }else{

                    if(selectedType=='Daily'||selectedType=='Hourly'){
                        handleHttpRequests(requestParams, requestParams.length-1, timeData, [], 0, requestParams.length);
                    }else if(selectedType.toLowerCase()=='status'){
                        handleStatusData();
                    }else if(selectedType.toLowerCase()=='history'){
                        multideviceParams=[];
                        selectedDevice.forEach(device=>{
                            requestParams.forEach(param=>{
                                multideviceParams.push({'device':device, 'param':param});
                            })
                        });
                        handleMultiDeviceHttpRequests(multideviceParams, multideviceParams.length-1, timeData, [], 0, multideviceParams.length);
                    }else{
                        newParams=[];
                        requestParams.forEach(param=>{
                            timeData.forEach(time=>{
                                t={};
                                t['param']=param;
                                t['time']=time;
                                newParams.push(t);
                            });
                        });
                        handleMonthAndYearlyHttpRequest(newParams, newParams.length-1, timeData, [], 0, newParams.length);
                    }
                }
            }
        }
  }

  /**
   * Generating the table column array and displayed text
   * As it is a mat-table, displayedcolumn should be unique according
   * to data key
   **/
  function generateTableData(){
      if(selectedDevice.length>1){
          displayedColumns=['DeviceName'];
          tableMeta=[{'key':'DeviceName', 'title':'Device Name'}];
      }else{
          if(selectedType.toLowerCase()=='history'){
            displayedColumns=['DeviceName'];
            tableMeta=[{'key':'DeviceName', 'title':'Device Name'}];
          }else{
            displayedColumns=['Date'];
            tableMeta=[{'key':'Date', 'title':'Date'}];
          }
      }
      settingsData=selectedDevice.length==1?self.ctx.settings.singleDeviceTableColumn:self.ctx.settings.multiDeviceOption=='B'?self.ctx.settings.multiDeviceTableColumn:self.ctx.settings.singleDeviceTableColumn;
      settingsData.sort(function (a, b) {
          return a.order - b.order;
        });
      settingsData.forEach(setting=>{
              column=setting.aggression.charAt(0).toUpperCase() + setting.aggression.slice(1).toLowerCase()+setting.key.charAt(0).toUpperCase() + setting.key.slice(1).toLowerCase();
              displayedColumns.push(column);
              tableMeta.push({'key':column, 'title':setting.title});
      });
      return {'displayedColumns':displayedColumns, 'tableMeta':tableMeta};
  }

  /**
   * Create the unix timestamp from
   * 1. Start date / start date and time
   * 2. End date / end date and time
   * Interval will be created according to the selected report type.
   **/
  function getTimeData(){
      unixList=[];
      if(selectedType=='Monthly'||selectedType=='Yearly'){
            startDate=new Date(self.ctx.$scope.formData.startDate);
            endDate=new Date(self.ctx.$scope.formData.endDate);
            startMonth=startDate.getMonth()+1;
            startYear=startDate.getFullYear();
            endMonth=endDate.getMonth()+1;
            endYear=endDate.getFullYear();
            firstIterate=true;
            if(selectedType=='Monthly'){
                for(;startYear<=endYear;startYear++){
                    em=12;
                    if(firstIterate){
                        firstIterate=false;
                    }else{
                        startMonth=1;
                    }
                    if(startYear==endYear){
                        em=endMonth;
                    }
                    for(;startMonth<=em;startMonth++){
                        s=startMonth>9?startMonth:'0'+startMonth;
                        sUnix=new Date(s+' 01 '+startYear+' 00:00:00').getTime();
                        eUnix=new Date(s+' '+new Date(startYear, startMonth, 0).getDate()+' '+startYear+' 23:59:59').getTime();
                        interval=eUnix-sUnix;
                        unixList.push({'startUnix': sUnix, 'endUnix':eUnix, 'interval':interval});
                    }
                }
            }else{
                startYear=startDate.getFullYear();
                endYear=endDate.getFullYear()
                for(;startYear<=endYear;startYear++){
                    sUnix=new Date('01 01 '+startYear+' 00:00:00').getTime();
                    eUnix=new Date('12 31 '+startYear+' 23:59:59').getTime();
                    interval=eUnix-sUnix;
                    unixList.push({'startUnix': sUnix, 'endUnix': eUnix, 'interval': interval});
                }
            }
            return unixList;
      }else{
            endDate=moment(self.ctx.$scope.formData.endDate).format('YYYY-MM-DD');
            startDate=moment(self.ctx.$scope.formData.startDate).format('YYYY-MM-DD');
            startUnix=selectedType=='Hourly'||selectedType=='history'?new Date(startDate+' '+self.ctx.$scope.formData.startTime).getTime():new Date(startDate+' 00:00:00').getTime();
            endUnix=selectedType=='Hourly'||selectedType=='history'?new Date(endDate+' '+self.ctx.$scope.formData.endTime).getTime():new Date(endDate+' 23:59:59').getTime();
            interval=selectedType=='history'?endUnix-startUnix:selectedType=='Hourly'?60*60*1000:24*60*60*1000;
            return {'startUnix':startUnix, 'endUnix':endUnix, 'interval':interval};
      }
  }


  /**
   * Formatting the settings data and make them list according to the 
   * aggregations. Each aggregation contains the key also
   **/
  function getFormattedSettingsData(){
      settingsData=selectedDevice.length==1?self.ctx.settings.singleDeviceTableColumn:self.ctx.settings.multiDeviceOption=='B'?self.ctx.settings.multiDeviceTableColumn:self.ctx.settings.singleDeviceTableColumn;
        aggregations=[];
        settingsData.forEach(setting=>{
            if(aggregations.indexOf(setting.aggression)<0){
                aggregations.push(setting.aggression);
            }
        });
        requestParams=[];
        if(aggregations.length>0){
            aggregations.forEach(aggregation=>{
                const param={agg:aggregation, key:[], decimalPlaces:[]};
                for(let index=0;index<settingsData.length;index++){
                    if(aggregation==settingsData[index].aggression){
                        param.key.push(settingsData[index].key);
                        param.decimalPlaces.push(settingsData[index].decimalPlace?settingsData[index].decimalPlace:0);
                    }
                }
                requestParams.push(param);
            });
        }
        return requestParams;
  }


  /**
   * This will handle all http requests
   * Basically it will a recursive function and function will call 
   * until the length meet to conditional statement
   **/
   function handleHttpRequests(requestParams, length, timeData, dataStack, progressInitVal, progressEndVal){
      self.ctx.$scope.progressPercent=Math.ceil((progressInitVal/progressEndVal)*100);
        self.ctx.detectChanges();
        progressInitVal=progressInitVal+1;
       if(length>=0){
           request=http.get(baseUrl+'api/plugins/telemetry/'+deviceData.datasource.entity.id.entityType+'/'+selectedDevice[0].id+'/values/timeseries?keys='+requestParams[length].key+'&endTs='+timeData.endUnix+'&startTs='+timeData.startUnix+'&interval='+timeData.interval+'&agg='+requestParams[length].agg, {headers:{"Content-Type":"application/json","X-Authorization":"Bearer "+token}}
                );
            request.subscribe(response=>{
                d={};
                d['agg']=requestParams[length].agg;
                d['key']=requestParams[length].key;
                d['response']=response;
                d['decimalPlace']=requestParams[length].decimalPlaces;
                dataStack.push(d);
                handleHttpRequests(requestParams, length-1, timeData, dataStack, progressInitVal, progressEndVal);
            });
       }else{
           AnalysisData(dataStack, timeData);
       }
   }


   /**
    * Handle http request for single device with report type monthly and yearly.
    * As every month and year will not contain same timeframe, we need to separate them from default.
    * It will handle a single device report request for monthly and yearly basis
    **/
    function handleMonthAndYearlyHttpRequest(requestParams, length, timeData, dataStack, progressInitVal, progressEndVal){
        self.ctx.$scope.progressPercent=Math.ceil((progressInitVal/progressEndVal)*100);
        self.ctx.detectChanges();
        progressInitVal=progressInitVal+1;
        if(length>=0){
          request=http.get(baseUrl+'api/plugins/telemetry/'+deviceData.datasource.entity.id.entityType+'/'+selectedDevice[0].id+'/values/timeseries?keys='+requestParams[length].param.key+'&endTs='+requestParams[length].time.endUnix+'&startTs='+requestParams[length].time.startUnix+'&interval='+requestParams[length].time.interval+'&agg='+requestParams[length].param.agg, {headers:{"Content-Type":"application/json","X-Authorization":"Bearer "+token}}
                );
            request.subscribe(response=>{
                d={};
                d['agg']=requestParams[length].param.agg;
                d['key']=requestParams[length].param.key;
                d['decimalPlace']=requestParams[length].param.decimalPlaces;
                d['time']=requestParams[length].time.startUnix+(requestParams[length].time.interval/2);
                d['response']=response;

                dataStack.push(d);
                handleMonthAndYearlyHttpRequest(requestParams, length-1, timeData, dataStack, progressInitVal, progressEndVal);
            });
      }else{
          AnalysisMonthAndYearData(dataStack);
      }
    }



    /**
     * Creating table visible modeled data from raw data found from http
     * request and response
     **/
     function AnalysisMonthAndYearData(dataStack){
         timeList=[];
         preFinalData=[];
         dataStack.forEach(singleData=>{
             sresp=singleData.response;
             if(timeList.indexOf(singleData.time)==-1){
                 timeList.push(singleData.time);
                 d={};
                 d['Date']=dateTimeConversion(singleData.time);
                 if(Object.keys(sresp).length>0){
                     for(let key in sresp){
                         dIndex=singleData.key.indexOf(key);
                         dataKey=singleData.agg.charAt(0).toUpperCase() + singleData.agg.slice(1).toLowerCase()+key.charAt(0).toUpperCase() + key.slice(1).toLowerCase();
                         d[dataKey]=Number(sresp[key][0].value).toFixed(singleData.decimalPlace[dIndex]>-1?singleData.decimalPlace[dIndex]:0);
                     }
                 }else{
                     singleData.key.forEach(k=>{
                         dIndex=singleData.key.indexOf(k);
                         dataKey=singleData.agg.charAt(0).toUpperCase() + singleData.agg.slice(1).toLowerCase()+k.charAt(0).toUpperCase() + k.slice(1).toLowerCase();
                         d[dataKey]=Number(0).toFixed(singleData.decimalPlace[dIndex]>-1?singleData.decimalPlace[dIndex]:0);
                     });
                 }
                 preFinalData.push(d);
             }else{
                 index=timeList.indexOf(singleData.time);
                 if(Object.keys(sresp).length>0){
                     for(let key in sresp){
                         dIndex=singleData.key.indexOf(key);
                         dataKey=singleData.agg.charAt(0).toUpperCase() + singleData.agg.slice(1).toLowerCase()+key.charAt(0).toUpperCase() + key.slice(1).toLowerCase();
                         preFinalData[index][dataKey]=Number(sresp[key][0].value).toFixed(singleData.decimalPlace[dIndex]>-1?singleData.decimalPlace[dIndex]:0);
                     }
                 }else{
                     singleData.key.forEach(k=>{
                         dIndex=singleData.key.indexOf(k);
                         dataKey=singleData.agg.charAt(0).toUpperCase() + singleData.agg.slice(1).toLowerCase()+k.charAt(0).toUpperCase() + k.slice(1).toLowerCase();
                         preFinalData[index][dataKey]=Number(0).toFixed(singleData.decimalPlace[dIndex]>-1?singleData.decimalPlace[dIndex]:0);
                     });
                 }
             }
         });
         handleDomElements(preFinalData.reverse());
     }

   /**
    * This will handle multi-devices https request with all aggregation
    * level. this will be a recursive function
    **/
   function handleMultiDeviceHttpRequests(requests,length, timeData, dataStack, progressInitVal, progressEndVal){
       self.ctx.$scope.progressPercent=Math.ceil((progressInitVal/progressEndVal)*100);
        self.ctx.detectChanges();
        progressInitVal=progressInitVal+1;
       if(length>=0){
          req=http.get(baseUrl+'api/plugins/telemetry/'+deviceData.datasource.entity.id.entityType+'/'+requests[length].device.id+'/values/timeseries?keys='+requests[length].param.key+'&endTs='+timeData.endUnix+'&startTs='+timeData.startUnix+'&interval='+timeData.interval+'&agg='+requests[length].param.agg,
                        {headers:{"Content-Type":"application/json","X-Authorization":"Bearer "+token}}
                        )
            req.subscribe(response=>{
                dataStack.push({
                    device: requests[length].device,
                    param: requests[length].param,
                    response:response
                });
                handleMultiDeviceHttpRequests(requests, length-1, timeData, dataStack, progressInitVal, progressEndVal);
            })
       }else{
           AnalysisMultiDeviceData(dataStack);
       }
   }
   function AnalysisData(dataStack, timeData){
        unixList=[];
        let t=timeData.startUnix+(timeData.interval/2);
        for(;t<= timeData.endUnix; t=t+timeData.interval){
            unixList.push(t);
        }
        data=[];
        dataStack.forEach(d=>{
            res=d.response;
            resp={};
            if(Object.keys(res).length){
                for(let key in res){
                    r=[];
                    unixList.forEach(unix=>{
                        q=res[key].find(i=>i.ts==unix);
                        if(typeof(q)=='object'){
                            r.push(q);
                        }else{
                            r.push({
                               'ts':unix,
                               'value':Number(0).toFixed(2)
                            });
                        }
                    });
                    resp[key]=r;
                }
            }else{
                r=[];
                keys=d.key;
                unixList.forEach(unix=>{
                    r.push({'ts':unix, 'value':Number(0).toFixed(2)});
                });
                keys.forEach(key=>{
                    resp[key]=r;
                });
            }
            data.push({
                agg:d.agg,
                key:d.key,
                decimalPlace:d.decimalPlace,
                response:resp,
            });
        });
        generateFinalOutput(data);
   }


   function AnalysisMultiDeviceData(dataStack){
       tableData=[];
       dataStack.forEach(data=>{
           d={}
           q=tableData.find(fData=>fData.DeviceId==data.device.id);
           if(typeof(q)=='object'){
               res=data.response;
               if(Object.keys(res).length>0){
                   for(let key in res){
                       dIndex=data.param.key.indexOf(key);
                       dataKey=data.param.agg.charAt(0).toUpperCase() + data.param.agg.slice(1).toLowerCase()+key.charAt(0).toUpperCase() + key.slice(1).toLowerCase();
                       q[dataKey]=Number(res[key][0].value).toFixed(data.param.decimalPlaces[dIndex]>-1?data.param.decimalPlaces[dIndex]:0);
                   }
               }else{
                   data.param.key.forEach(k=>{
                       dIndex=data.param.key.indexOf(k);
                       dataKey=data.param.agg.charAt(0).toUpperCase() + data.param.agg.slice(1).toLowerCase()+k.charAt(0).toUpperCase() + k.slice(1).toLowerCase();
                       q[dataKey]=Number(0).toFixed(data.param.decimalPlaces[dIndex]>-1?data.param.decimalPlaces[dIndex]:0);
                   })
               }
           }else{
               d['DeviceName']=data.device.name;
               d['DeviceId']=data.device.id;
               res=data.response;
               if(Object.keys(res).length>0){
                   for(let key in res){
                       dIndex=data.param.key.indexOf(key);
                       dataKey=data.param.agg.charAt(0).toUpperCase() + data.param.agg.slice(1).toLowerCase()+key.charAt(0).toUpperCase() + key.slice(1).toLowerCase();
                       d[dataKey]=Number(res[key][0].value).toFixed(data.param.decimalPlaces[dIndex]>-1?data.param.decimalPlaces[dIndex]:0);
                   }
               }else{
                   data.param.key.forEach(k=>{
                       dIndex=data.param.key.indexOf(k);
                       dataKey=data.param.agg.charAt(0).toUpperCase() + data.param.agg.slice(1).toLowerCase()+k.charAt(0).toUpperCase() + k.slice(1).toLowerCase();
                       d[dataKey]=Number(0).toFixed(data.param.decimalPlaces[dIndex]>-1?data.param.decimalPlaces[dIndex]:0);
                   });
               }
               tableData.push(d);
           }
       });
       if(tableData.length>0){
           finalData=[];
           tableData.forEach(dt=>{
                tmpData={};
                generateTableData().displayedColumns.forEach(tdata=>{
                    if(Object.keys(dt).indexOf(tdata)<0){
                        tmpData[tdata]=0.00;
                    }else{
                        tmpData[tdata]=dt[tdata];
                    }
                });
                finalData.push(tmpData);
            });
            handleDomElements(finalData);
       }
   }

   /**
    * This function will generate table data to show inside the table
    * rendered in html
    **/
    function generateFinalOutput(finalData){
        tableData=[];
        first=true;
        finalData.forEach(final=>{
            response=final.response;
            for(let key in response){
                dIndex=final.key.indexOf(key);
                dataKey=final.agg.charAt(0).toUpperCase() + final.agg.slice(1).toLowerCase()+key.charAt(0).toUpperCase() + key.slice(1).toLowerCase();
                if(first){
                    first=false;
                    for(let i=0;i<response[key].length;i++){
                        d={};
                        d['Date']=dateTimeConversion(response[key][i].ts);
                        d[dataKey]=Number(response[key][i].value).toFixed(final.decimalPlace[dIndex]>=0?final.decimalPlace[dIndex]:0);
                        tableData.push(d);
                    }
                }else{
                    for(let i=0;i<response[key].length;i++){
                        tableData[i][dataKey]=Number(response[key][i].value).toFixed(final.decimalPlace[dIndex]>=0?final.decimalPlace[dIndex]:0);
                    }
                }
            }
        });
        finalData=[];
        tableData.forEach(dt=>{
            tmpData={};
            generateTableData().displayedColumns.forEach(tdata=>{
                if(Object.keys(dt).indexOf(tdata)<0){
                    tmpData[tdata]=0;
                }else{
                    tmpData[tdata]=dt[tdata];
                }
            });
            finalData.push(tmpData);
        });
        handleDomElements(finalData);
    }

    /**
     * Status data for multi device option.
     * status data comes from the defaultSubscription.data
     **/
    function handleStatusData(){
        statusColumn=self.ctx.settings.statusTableColumn;
        keys=['deviceName'];
        tableMeta=[{'title':'Device name', 'key':'deviceName'}];
        data=[];
        statusColumn.forEach(col=>{
            c=latestData.filter(lData=>lData.dataKey.name==col.key);
            if(c.length>0){
                if(keys.indexOf(c[0].dataKey.name)==-1){
                    keys.push(c[0].dataKey.name);
                    tableMeta.push({
                        'title': c[0].dataKey.label,
                        'key': c[0].dataKey.name
                    });
                }
            }else{
                keys.push(col.key);
                tableMeta.push({
                        'title': col.key+' (invalid key)',
                        'key': col.key
                    });
            }
        })
        latestData.forEach(x=>{
            if(statusColumn.filter(col=>col.key==x.dataKey.name).length>0){
                data.push({
                    "deviceName":x.datasource.name,
                    "dataKey":x.dataKey.name,
                    "data":Number(x.data[0][1]).toFixed(2),
                });
            }
        });
        tableData=[];
        for(i=0;i<selectedDevice.length;i++){
            q={};
            q.deviceName=selectedDevice[i].name;
            data.forEach(sd=>{
                if(selectedDevice[i].name==sd.deviceName){
                    q[sd.dataKey]=sd.data;
                }
            });
            tableData.push(q);
        }
        l=[];
        statusColumn.forEach(col=>l.push(col.key));
        if(l.length>0){
            l.forEach(col=>{
                tKeys=Object.keys(tableData[0]);
                for(let x=0;x<tableData.length;x++){
                    if(tKeys.indexOf(col)==-1){
                        tableData[x][col]='Invalid';
                    }
                }
            });
        }
        self.ctx.$scope.displayedColumns=keys;
        self.ctx.$scope.tableMeta=tableMeta;
        handleDomElements(tableData);
    }


    /**
     * Controlling the html DOM elements using this function
     * disable progress, show table, changed table data will be updated
     * from here
     **/
    function handleDomElements(tableData){
        self.ctx.$scope.tableData=tableData;
        self.ctx.$scope.showProgress=false;
        self.ctx.$scope.showTable=true;
        self.ctx.$scope.selectedType=selectedType;
        self.ctx.$scope.startDate=$('#startDate').val();
        self.ctx.$scope.endDate=$('#endDate').val();
        self.ctx.$scope.startTime=$("#startTime").val();
        self.ctx.$scope.endTime=$("#endTime").val();
        self.ctx.detectChanges();
    }

  /**
   * Exporting the searched data in csv format.
   * It will take query information and then place the searched data
   **/
    self.ctx.$scope.exportAsCSV=function(){
        const items = self.ctx.$scope.tableData
        const replacer = (key, value) => value === null ? '' : value // specify how you want to handle null values here
        const header = Object.keys(items[0]);
        let csv = items.map(row => header.map(fieldName => JSON.stringify(row[fieldName], replacer)).join(','));
        cHeader=[];
        tMeta= self.ctx.$scope.tableMeta;
        header.forEach(h=>{
            if(tMeta.filter(data=>data.key==h).length>0){
                cHeader.push(tMeta.filter(data=>data.key==h)[0].title);
            }
        });
        csv.unshift(cHeader.join(','));
        csv.unshift([].join(','));
        if(selectedType.toLowerCase()!='status'){
            csv.unshift(['End time', $('#endDate').val(),$('#endTime').val()].join(','));
            csv.unshift(['Start time', $('#startDate').val(),$('#startTime').val()].join(','));
        }
        csv.unshift(['Report Type', selectedType].join(','));
        dv=selectedDevice.length>1?'Multiple devices':selectedDevice[0].name
        csv.unshift(['Device Name', dv].join(','));
        csv = csv.join('\r\n')

        let link = document.createElement("a");    
        link.id="lnkDwnldLnk";
        document.body.appendChild(link);
        blob = new Blob([csv], { type: 'text/csv' }); 
        let csvUrl = window.webkitURL.createObjectURL(blob);
        let filename = 'IoT_Mate_Export_'+moment(new Date()).format('DD_MM_YYYY')+'.csv';
        jQuery("#lnkDwnldLnk")
        .attr({
            'download': filename,
            'href': csvUrl
        });
        jQuery('#lnkDwnldLnk')[0].click();
        document.body.removeChild(link);
    }


    /**
     * Convert the unix format to human readable date time format
     **/
    function dateTimeConversion(dateData){
      return selectedType=='Hourly'?moment(dateData).format("DD MMM YYYY, h A"):selectedType=='Daily'?moment(dateData).format("DD MMM YYYY"):selectedType=='Monthly'?moment(dateData).format("MMM YYYY"):moment(dateData).format("YYYY");
    }
}

descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "Settings",
        "required": [
            "singleDeviceTableColumn"
        ],
        "properties": {
            "singleDeviceTableColumn": {
                "type": "array",
                "title": "Single Device Table Column",
                "maxItems": 20,
                "items": {
                    "type": "object",
                    "properties": {
                        "title": {
                            "title": "Title",
                            "type": "string"
                        },
                        "key": {
                            "title": "key",
                            "type": "string"
                        },
                        "aggression": {
                            "title": "aggression",
                            "type": "string",
                            "enum": [
                                "MIN",
                                "MAX",
                                "AVG",
                                "COUNT",
                                "SUM",
                                "INC"
                            ]
                        },
                        "order": {
                            "title": "Order",
                            "type": "number"
                        },
                        "decimalPlace": {
                            "title": "Decimal Place",
                            "type": "number"
                        }
                    },
                    "required": [
                        "key",
                        "aggression"
                    ]
                }
            },
            "multiDeviceOption": {
              "title": "Multi Device Option",
              "type": "string"
            },
            "multiDeviceTableColumn": {
