*{
    padding: 0;
    margin: 0;
    box-sizing: border-box;
}
.ctl-btn{
    position: absolute;
    left:0;
    top:0;
    height:72px;
}
.wrapper{
    overflow: auto;
    position: relative;
}
.room-temp{
    position: absolute;
    left: 0;
    top: 0;
    border: 1px solid #6e6e6e;

}
.room-temp-in{
    display: flex;
    justify-content: space-between;
    align-items: center;
}
.room-temp-in div{
    font-size: 20px;
    padding: 5px;
}
.step{
    width: 100%;
    margin: 0 auto;
}
.step .step-container ul{
    text-align: center;
    display: flex;
    justify-content: center;
    list-style: none;
    margin: 0 auto;
}

.step .step-box{
    text-align: center;
    border-radius: 10px;
    padding: 10px;
    color: white;
    position: relative;
}
.step-box h2{
    font-weight: 800;
    font-size: 25px;
}
.step-box p{
    font-size: 15px;
}
.connector{
    height: 100px;
    position: relative;
}
.vertical-line{
    position: absolute;
    top: 0;
}

/*============= loading spinner ====================*/
/*.list{*/
/*    display: none;*/
/*}*/
.lds-ellipsis {
  display: inline-block;
  position: relative;
  width: 80px;
  height: 80px;

}
.lds-ellipsis div {
  position: absolute;
  top: 33px;
  width: 13px;
  height: 13px;
  border-radius: 50%;
  background: #fff;
  animation-timing-function: cubic-bezier(0, 1, 1, 0);
}
.lds-ellipsis div:nth-child(1) {
  left: 8px;
  animation: lds-ellipsis1 0.6s infinite;
}
.lds-ellipsis div:nth-child(2) {
  left: 8px;
  animation: lds-ellipsis2 0.6s infinite;
}
.lds-ellipsis div:nth-child(3) {
  left: 32px;
  animation: lds-ellipsis2 0.6s infinite;
}
.lds-ellipsis div:nth-child(4) {
  left: 56px;
  animation: lds-ellipsis3 0.6s infinite;
}
@keyframes lds-ellipsis1 {
  0% {
    transform: scale(0);
  }
  100% {
    transform: scale(1);
  }
}
@keyframes lds-ellipsis3 {
  0% {
    transform: scale(1);
  }
  100% {
    transform: scale(0);
  }
}
@keyframes lds-ellipsis2 {
  0% {
    transform: translate(0, 0);
  }
  100% {
    transform: translate(24px, 0);
  }
}
