descriptor.controllerScript:
self.onInit = function() {
    self.ctx.deviceList={
        truckUnload: 'f8cf0180-dce9-11ed-afaa-333332abe82d',
        dieselFlowInGen1: 'fa073360-dce9-11ed-afaa-333332abe82d',
        dieselFlowInGen2: 'fb3f6540-dce9-11ed-afaa-333332abe82d',
        dieselFlowInGen3: 'fc777010-dce9-11ed-afaa-333332abe82d',
        dieselFlowInGen4: 'fdafc900-dce9-11ed-afaa-333332abe82d',
        dieselFlowInGen5: 'fee7acc0-dce9-11ed-afaa-333332abe82d',
        dieselFlowReturnGen1: 'ed449960-dce9-11ed-afaa-333332abe82d',
        dieselFlowReturnGen2: 'ee7c07f0-dce9-11ed-afaa-333332abe82d',
        dieselFlowReturnGen3: 'efb3ebb0-dce9-11ed-afaa-333332abe82d',
        dieselFlowReturnGen4: 'f0ec6bb0-dce9-11ed-afaa-333332abe82d',
        generator1: '6181dbf0-d068-11ed-8b01-738772f9cc16',
        generator2: '6349cab0-d068-11ed-8b01-738772f9cc16',
        generator3: '6513dc50-d068-11ed-8b01-738772f9cc16',
        generator4: '66de1500-d068-11ed-8b01-738772f9cc16',
        generator5: 'dd86f7f0-d5cf-11ed-afaa-333332abe82d'
    }
    self.ctx.runTimeDevices={
        generator1: '331d8351-ee2b-11ed-afaa-333332abe82d',
        generator2: '331ce710-ee2b-11ed-afaa-333332abe82d',
        generator3: '331cc000-ee2b-11ed-afaa-333332abe82d',
        generator4: '331d8352-ee2b-11ed-afaa-333332abe82d',
        generator5: '331c4ad0-ee2b-11ed-afaa-333332abe82d'
    }
    const token = localStorage.getItem('jwt_token');
    const $scope=self.ctx.$scope;
    $scope.deviceList=self.ctx.deviceList;
    $scope.runTimeDevices=self.ctx.runTimeDevices;
    $scope.lineStrokeColor='#00bedd';
    $scope.stepBoxBgColor='blue'
    $scope.energyUnit='kWh';
    $scope.flowUnit='L'
    $scope.dataMode='history';
    $scope.color=self.ctx.widgetConfig.color;
    $scope.settings=self.ctx.settings;
    $scope.reportTypes=['Today', 'Daily', 'Monthly', 'Yearly', 'Custom'];
    $scope.today=new Date();
    let selectType = "Today";
    $scope.selectType = selectType;
    $scope.selectedType='Today';
    let selectedStartDate,selectedEndDate;
    if($('.step-box')){
        $('.step-box').each(function(index){
            $(this).click(function(event){
                if($(this).data('nodeid')){
                    // if($scope.dataMode=='realtime'){
                        dvc=self.ctx.data.filter(dvcD=>dvcD.datasource.entity.id.id==$(this).data('nodeid'));
                        const actionDesc=$(this).data('nodetype')=='diesel'?flowActionDescriptor[0]:energyActionDescriptor[0];
                        self.ctx.actionsApi.handleWidgetAction(event, actionDesc, {entityType: 'DEVICE', id: $(this).data('nodeid')}, dvc[0].datasource.entityLabel);
                    // }
                }
            }); 
            $(this).hover(()=>{
                if($(this).data('nodeid')){
                    $(this).addClass('hover');
                }
            }, ()=>{
                $(this).removeClass('hover');
            });
        });
    }


    let flowActionDescriptor = self.ctx.actionsApi.getActionDescriptors('viewFlowDetails');
    let energyActionDescriptor = self.ctx.actionsApi.getActionDescriptors('viewEnergyDetails');
    $scope.flowActionDescriptor=flowActionDescriptor;
    $scope.energyActionDescriptor=energyActionDescriptor;
    $scope.openDatePicker=function(dp) {
        dp.open();
    }

    //Monthly
    $scope.selectStartDate=function(eventData, dp, format) {
        selectedStartDate = eventData;
        $("#startDate").val(moment(eventData).format(format));

        loadGeneratorData(selectedStartDate.getTime(), selectedStartDate.setMonth(selectedStartDate.getMonth()+1));
        self.ctx.detectChanges();
        dp.close();
    }
    //Daily / Custom
    $scope.startDateChange=function($event){
        selectedStartDate = $event.value;
        startDt=new Date(moment(selectedStartDate).format('YYYY-MM-DD 06:00:00'));
        if(selectType=='Daily'){
            loadGeneratorData(startDt.getTime(), startDt.setDate(startDt.getDate()+1));
        }else if(selectType=='Custom'){
            if(selectedEndDate){
                endDt=new Date(moment(selectedEndDate).format('YYYY-MM-DD 06:00:00'));
                loadGeneratorData(startDt.getTime(), endDt.setDate(endDt.getDate()+1));
            }
        }
        self.ctx.detectChanges();
    }
    //Custom
    $scope.endDateChanege=function($event) {
        selectedEndDate = $event.value;
        if(selectedStartDate){
            startDt=new Date(moment(selectedStartDate).format('YYYY-MM-DD 06:00:00'));
            endDt=new Date(moment(selectedEndDate).format('YYYY-MM-DD 06:00:00'));
            loadGeneratorData(startDt.getTime(), endDt.setDate(endDt.getDate()+1));
        }
        self.ctx.detectChanges();
    }

    //Yearly
    $scope.selectYear=function($event, dp, format){
        start=new Date(moment($event).format('YYYY-MM-DD 06:00:00'));
        selectedStartDate=start;
        $("#startDate").val(moment($event).format(format));
        loadGeneratorData(start.getTime(), start.setFullYear(start.getFullYear()+1));
        dp.close();
    }
    $scope.typeSelect = function($event){
        selectType = $event.value;
        $scope.selectedType=selectType;
        if(selectType=='Today'){
            let startDateTs=new Date(moment(new Date()).format('YYYY-MM-DD 06:00:00'));
            if(new Date(moment(new Date()).format('YYYY-MM-DD 00:00:00')).getTime()<now.getTime()&&new Date(moment(new Date()).format('YYYY-MM-DD 06:00:00')).getTime()>=now.getTime()){
                startDateTs.setDate(startDateTs.getDate()-1);
            }
            loadGeneratorData(startDateTs.getTime(), new Date().getTime());
        }
    }

    $scope.toggleMonitoring=function(){
        $scope.dataMode=$scope.dataMode=='history'?'realtime':'history';
        if($scope.dataMode=='history'){
            loadGeneratorData(new Date(moment(new Date()).format('YYYY-MM-DD 06:00:00')).getTime(), new Date().getTime());
            $scope.flowUnit='L';
            $scope.energyUnit='kWh'
        }else{
            self.onDataUpdated();
            $scope.selectedType='Today';
            $scope.selectType='Today';
            $scope.flowUnit='L/h';
            $scope.energyUnit='kW';
        }
        self.ctx.detectChanges();
    }
    const now=new Date();
    $scope.data={};
    $scope.isLoading=true;
    Object.keys(self.ctx.deviceList).forEach(key=>{
        $scope.data[key]=0.00
    });
    today=new Date();
    let startDateTs=new Date(moment(new Date()).format('YYYY-MM-DD 06:00:00'));
    if(new Date(moment(new Date()).format('YYYY-MM-DD 00:00:00')).getTime()<now.getTime()&&new Date(moment(new Date()).format('YYYY-MM-DD 06:00:00')).getTime()>=now.getTime()){
        startDateTs.setDate(startDateTs.getDate()-1);
    }
    loadGeneratorData(startDateTs.getTime(), now.getTime());
    function loadGeneratorData(startTs, endTs){
        $scope.isLoading=true;
        Object.keys(self.ctx.deviceList).forEach((key, index)=>{
            const req=self.ctx.http.post('/nrdapi/get_daily_consumption',  {start_ts: startTs, end_ts: endTs, entity_id: self.ctx.deviceList[key]},  {headers: {"X-Authorization": 'Bearer '+token}});
            req.subscribe(res=>{
                if(index==Object.keys(self.ctx.deviceList).length-1){
                    $scope.isLoading=false;
                }
                total=0;
                if(res.length){
                    res.forEach(r=>{
                        total+=r.net_kwh;
                    });

                }
                $scope.data[key]=Number(total.toFixed(2));
                self.ctx.detectChanges();
            }, error=>{
                if(index==Object.keys(self.ctx.deviceList).length-1){
                    $scope.isLoading=false;
                    self.ctx.detectChanges();
                }
            });
        });
    }

    $(function() {
        $(".step-box").contextmenu(function(e) {
            deleteAllElementByClassName();

            // ==========================
            if($(this).data('runtimeid')==undefined){
                return true;
            }else{
                let x = (parseInt(e.pageX)-parseInt($(this).offset().left)) + 'px';
                let y = (parseInt(e.pageY)-parseInt($(this).offset().top)) + 'px';
                let td='';
                startTs=new Date(moment().format('YYYY-MM-DD 06:00:00')).getTime();
                endTs=new Date().getTime();
                td+='<tr><td>Runtime</td><td id="runtime"> :'+getRuntimeInHMS(0)+'</td></tr>';
                let innerDiv = $(`
                    <div class="inner-content">
                        <div class="lds-ellipsis"><div></div><div></div><div></div><div></div></div>
                        <table class="list">
                            <tbody>`+td+`
                            </tbody>
                        </table>
                    </div>
                `);

                innerDiv.css({
                  "background-color": "black",
                  "color":"white",
                  "font-size":"16px",
                  "opacity":0.8,
                  "width": "auto",
                  "height": "80px",
                  "padding": "5px",
                  "border-radius":"4px",
                  "display": "flex",
                  "align-items": "center"
                });


                let div = $('<div class="content">').css({
                  "position": "absolute",
                  "left": x,
                  "top": y,
                  "overflow": 'hidden',
                  "z-index": "100",
                  "text-align": "left"
                });
                div.append(innerDiv);
                $(this).append(div);


                let contentwidth = $(".content").width(); //Here your image width
                let contentheight = $(".content").height(); // Here your image height

                if((parseInt(e.pageX)+contentwidth)> $('body').width()){
                    x = (parseInt(e.pageX)-parseInt($(this).offset().left))-contentwidth;
                }
                if((parseInt(e.pageY)+contentheight)> $('body').height())
                    y=(parseInt(e.pageY)-parseInt($(this).offset().top))-contentheight;
                $(".content").css({
                    "left": x,
                    "top": y
                });
                $(".list").css({
                    "display":"none"
                });
                startTs=new Date(moment().format('YYYY-MM-DD 06:00:00')).getTime();
                endTs=new Date().getTime();
                if($scope.selectedType=='Daily'){
                    startTs=new Date(moment(selectedStartDate).format('YYYY-MM-DD 06:00:00')).getTime();
                    let ed=new Date(moment(selectedStartDate).format('YYYY-MM-DD 06:00:00'));
                    ed.setDate(ed.getDate()+1);
                    endTs=new Date(moment(ed).format('YYYY-MM-DD 06:00:00')).getTime();
                }else if($scope.selectedType=='Monthly'){
                    tyear=new Date(selectedStartDate.getTime());
                    tyear.setMonth(tyear.getMonth()-1);
                    startTs=tyear.getTime();
                    endTs=selectedStartDate.getTime();
                }else if($scope.selectedType=='Yearly'){
                    tyear=new Date(selectedStartDate.getTime());
                    tyear.setFullYear(tyear.getFullYear()-1);
                    startTs=tyear.getTime();
                    endTs=selectedStartDate.getTime();
                }else if($scope.selectedType=='Custom'){

                    startTs=new Date(moment(selectedStartDate).format('YYYY-MM-DD 06:00:00')).getTime();
                    endTs=new Date(moment(selectedEndDate).format('YYYY-MM-DD 06:00:00')).getTime();
                }
                const req=self.ctx.http.post('/nrdapi/get_daily_consumption',  {start_ts: startTs, end_ts: endTs, entity_id: $(this).data('runtimeid')},  {headers: {"X-Authorization": 'Bearer '+token}});
                req.subscribe(res=>{
                    $(".lds-ellipsis").css({
                    "display":"none"
                    });
                    $(".list").css({
                        "display":"block"
                    });
                    let total=0;
                    if(res.length){
                        res.forEach(r=>{
                            total+=r.net_kwh;
                        });
                    }
                    $('#runtime').text(getRuntimeInHMS(total));
                })
                return false;

            }
      });
      $("body").click(function(e){
            deleteAllElementByClassName();
      })
    });
    function getRuntimeInHMS(totalRuntime){
        hours=Math.floor(totalRuntime/3600);
        minute=Math.floor((totalRuntime%3600)/60);
        seconds=Math.floor(((totalRuntime%3600)/60)%60);
        return (hours>9?hours:'0'+hours)+':'+(minute>9?minute:'0'+minute)+':'+(seconds>9?seconds:'0'+seconds);
    }
    const deleteAllElementByClassName = () => {
        // delete all existing content
        let elements = document.querySelectorAll('.content');

        // Iterate over the elements and remove them from their parent
        elements.forEach(function(element) {
            let parent = element.parentNode;
            parent.removeChild(element);
        });
    }

    self.onResize();

}
self.onDataUpdated=function(){
    $scope=self.ctx.$scope;
    clearInterval(self.ctx.intervals);
    if(self.ctx.data.length){
        if(self.ctx.data[0].data.length){
            let temperature=self.ctx.data.filter(dt=>dt.dataKey.name=='temperature');
            if(temperature.length){
                $scope.temperature=temperature[0].data[0][1].toFixed(2)
            }
            statusData={};
            Object.keys(self.ctx.deviceList).forEach(key=>{
                statusData[key]=false;
                dvc=self.ctx.data.filter(dvcD=>dvcD.datasource.entity.id.id==self.ctx.deviceList[key]);
                if(dvc.length){
                    if(dvc[0].datasource.entityFilter.deviceType=='flow-meter'){
                        statusData[key]=dvc[0].data[0][1]==0?false:true;
                    }
                    if($scope.dataMode=='realtime'){
                        $scope.data[key]=Number(dvc[0].data[0][1]);
                        self.ctx.detectChanges();
                    }
                }
            });
            runningDevice={};
            Object.keys(self.ctx.runTimeDevices).forEach((key, index)=>{
                dvc=self.ctx.data.filter(dvcD=>dvcD.datasource.entity.id.id==self.ctx.runTimeDevices[key]);

                runningDevice[key]=dvc[0].data[0][1];
            })
            let r=0;
            self.ctx.intervals=setInterval(function(){
                r+=10
                Object.keys(runningDevice).forEach((key, index)=>{
                    if(runningDevice[key]=='true'){
                        $("#svg"+(index+1)).css('transform', 'rotate('+r+'deg)')
                    }
                });
                if(r==100)r=10;
            }, 100);
            self.ctx.detectChanges();
        }
    }
}
self.onDestroy=function(){
    clearInterval(self.ctx.intervals);
}
self.onResize=function(){
    const $scope=self.ctx.$scope;
    $scope.height=self.ctx.height-94;
    $scope.width=(self.ctx.width<1000?1000:self.ctx.width)-12;
    $scope.sboxw=self.ctx.width>1440?250:self.ctx.width>1200?200:180;

    self.ctx.detectChanges();

    $('.tb-default .satl-report-ff .mat-form-field-appearance-outline .mat-form-field-outline').css('color',self.ctx.widgetConfig.color);
    $(document).ready(function() {
        $('.tb-default mat-form-field.satl-report-ff .mat-form-field-appearance-legacy .mat-form-field-underline').css('background-color', self.ctx.widgetConfig.color);
        $('.mat-form-field-appearance-outline .mat-form-field-outline').css('color',self.ctx.widgetConfig.color);

    });
}
self.actionSources = function() {
     return {
         'viewFlowDetails': {name: 'View flow details',multiple: false},
         'viewEnergyDetails': {name: 'View energy details',multiple: false},
     };
 }

