descriptor.controllerScript:
self.onInit = function() {
    const $scope=self.ctx.$scope;

}
self.onDataUpdated=function(){
    const $scope=self.ctx.$scope;
    const token = localStorage.getItem('jwt_token');
    let dList=[
        {dieselIn: "FM_13010", dieselRtn: "FM_13015", dieselRtTime: "RT_51001"},
        {dieselIn: "FM_13011", dieselRtn: "FM_13016", dieselRtTime: "RT_51002"},
        {dieselIn: "FM_13012", dieselRtn: "FM_13017", dieselRtTime: "RT_51003"},
        {dieselIn: "FM_13013", dieselRtn: "FM_13018", dieselRtTime: "RT_51004"},
        {dieselIn: "FM_13014", dieselRtn: "", dieselRtTime: "RT_51005"},
    ]
    let fList=[
        {furnaceOilIn: 'FM_13002', furnaceOilRtn: 'FM_13003', furnaceOilRtTime: 'RT_52001'},
        {furnaceOilIn: 'FM_13004', furnaceOilRtn: 'FM_13005', furnaceOilRtTime: 'RT_52002'}
    ]
    let eList=[
        {name: 'EM_11016'}
    ];
    if(self.ctx.data.length){
        if(self.ctx.data[0].data!=undefined){
            let deviceData=[];
            self.ctx.data.forEach(td=>{
                let total=0;
                td.data.forEach(tdd=>{
                    total+=Number(tdd[1]);
                });
                deviceData.push({name: td.datasource.name, label: td.datasource.entityLabel, data:total, color: td.dataKey.color});
            });
            let efficiency=[];
            let totalDieselEfficiency=0;
            let furnaceOilTotal=0;
            let electricityTotal=0;
            dList.forEach(diesel=>{
                i=deviceData.filter(dd=>dd.name==diesel.dieselIn);
                rtn=deviceData.filter(dd=>dd.name==diesel.dieselRtn);
                rtTime=deviceData.filter(dd=>dd.name==diesel.dieselRtTime);
                let actDiesel=Number(i[0]?.data);
                if(i.length && rtn.length){
                    actDiesel-=Number(rtn[0].data);
                }
                let eff=0;
                if (rtTime[0]?.data){
                    eff=Number(actDiesel/(Number(rtTime[0]?.data)/3600))
                }
                totalDieselEfficiency+=eff;
                efficiency.push({name:i[0]?.name, label: i[0]?.label, ef: eff})
            });
            fList.forEach(furnace=>{
                i=deviceData.filter(dd=>dd.name==furnace.furnaceOilIn);
                rtn=deviceData.filter(dd=>dd.name==furnace.furnaceOilRtn);
                if(i.length){
                    furnaceOilTotal+=Number(i[0].data);
                }
                if(rtn.length){
                    furnaceOilTotal-=Number(rtn[0].data);
                }
            });
            eList.forEach(electricity=>{
                i=deviceData.filter(dd=>dd.name==electricity.name);
                if(i.length){
                    electricityTotal+=Number(i[0].data);
                }
            });
            $scope.dieselEfficiency=Number((totalDieselEfficiency/efficiency.length).toFixed(2));
            self.ctx.detectChanges();
            const req=self.ctx.http.post('/nrdapi/get_production_data', {start_ts:self.ctx.timeWindow.minTime, end_ts: self.ctx.timeWindow.maxTime}, {headers: {"X-Authorization": 'Bearer '+token}});
            req.subscribe(resd=>{
                let totalProduction=0;
                resd.forEach(prdt=>{
                    totalProduction+=Number(prdt.dbl_v);
                });
                $scope.furnaceOilEfficiency=Number((furnaceOilTotal/totalProduction).toFixed(2));
                $scope.electricityEfficiency=Number((electricityTotal/totalProduction).toFixed(2));
                self.ctx.detectChanges();
            });
        }
    }
}

