self.onInit = function() {
    $(document).ready(function(){
      self.onResize();
    });

    const $scope = self.ctx.$scope;
    $scope.maxEndDate = new Date();
    $scope.maxStartDate = new Date();
    $scope.color = self.ctx.widgetConfig.color;
    $scope.buttonBgColor = self.ctx.widgetConfig.backgroundColor;
    $scope.settings=self.ctx.settings;
    $scope.reportTypes=['Today', 'Daily', 'Monthly', 'Yearly', 'Custom'];
    $scope.selectedType='Today';
    // selectTodayType();
    $scope.selectedStartDate;
    $scope.selectedEndDate;
    // $scope.minStartDate = $scope.selectedStartDate;
    let token = localStorage.getItem('jwt_token');


    $scope.openDatePicker=function(dp) {
        dp.open();
    }
    $scope.selectStartDate=function(eventData, dp, format) {
        $scope.selectedStartDate = eventData;
        $("#startDate").val(moment(eventData).format(format));

        if($scope.selectedType==='Monthly'){
            let startTime = new Date(moment($scope.selectedStartDate).format('YYYY-MM-DD 06:00:00'));
            self.ctx.timewindowFunctions.onUpdateTimewindow(startTime.getTime(),startTime.setMonth(startTime.getMonth()+1));
        }
        if($scope.selectedType==='Yearly'){
            let startTime = new Date(moment($scope.selectedStartDate).format('YYYY-MM-DD 06:00:00'));
            let endTime = new Date(moment(startTime).add(1, 'year'));
            self.ctx.timewindowFunctions.onUpdateTimewindow(startTime.getTime(),endTime.getTime());
        }
        self.ctx.detectChanges();
        dp.close();
    }
    $scope.startDateChanege=function($event){
        $scope.selectedStartDate = $event.value;

        if($scope.selectedType==='Daily'){
            let startTime = new Date(moment($scope.selectedStartDate).format('YYYY-MM-DD 06:00:00'))
            self.ctx.timewindowFunctions.onUpdateTimewindow(startTime.getTime(),startTime.setDate(startTime.getDate()+1));
        }else{
            if($scope.selectedEndDate){
                let startTime = new Date(moment($scope.selectedStartDate).format('YYYY-MM-DD 06:00:00'));
                let endTime = new Date(moment($scope.selectedEndDate).format('YYYY-MM-DD 06:00:00'));
                self.ctx.timewindowFunctions.onUpdateTimewindow(startTime.getTime(),endTime.getTime());
            }
        }
        self.ctx.detectChanges();
    }
    $scope.endDateChanege=function($event) {
        $scope.selectedEndDate = $event.value;
        $scope.maxStartDate = $scope.selectedEndDate;
        if($scope.selectedStartDate){
            let startTime = new Date(moment($scope.selectedStartDate).format('YYYY-MM-DD 06:00:00'));
            let endTime = new Date(moment($scope.selectedEndDate).format('YYYY-MM-DD 06:00:00'));
            self.ctx.timewindowFunctions.onUpdateTimewindow(startTime.getTime(),endTime.getTime());
        }
        self.ctx.detectChanges();
    }
    let selectTodayType = () => {
        // let currentHour = new Date(new Date().getTime()-(12*60*60*1000)).getHours();
        let currentHour = new Date().getHours();
        let startTime, endTime;
        if(currentHour>=0 && currentHour<=6){
            endTime = new Date(moment(new Date()).format('YYYY-MM-DD 06:00:00'));
            startTime = new Date(moment(endTime).subtract(1, 'days').format('YYYY-MM-DD 06:00:00'));
        }else{
            startTime = new Date(moment(new Date()).format('YYYY-MM-DD 06:00:00'));
            endTime = new Date(moment(startTime).add(1, 'days').format('YYYY-MM-DD 06:00:00'));
        }
        self.ctx.timewindowFunctions.onUpdateTimewindow(startTime.getTime(),endTime.getTime());
    }
    selectTodayType();

    $scope.typeSelect = function($event){
        selectType = $event.value;
        $scope.selectedType=selectType;
        $scope.maxStartDate = new Date();

        if(selectType=='Today'){
            selectTodayType();
        }
    }

    $scope.openDatePicker=(picker)=>{
        picker.open();
    }
}

self.onDataUpdated = function() {
    // const $scope=self.ctx.$scope;
    // let token = localStorage.getItem('jwt_token');
    // if(self.ctx.data.length){
    //     if(self.ctx.data[0].data.length){
    //         $scope.queryform.get('startDate').setValue(new Date(self.ctx.data[0].data[self.ctx.data[0].data.length-1][0]));
    //         const strt=new Date(moment(new Date(self.ctx.data[0].data[0][0])).format('YYYY-MM-DD 00:00:00')).getTime()
    //         let endt=new Date(moment(new Date(self.ctx.data[0].data[0][0])).format('YYYY-MM-DD 23:59:59')).getTime();
    //         prdt=self.ctx.data.filter(pr=>pr.dataKey.name=='ts_product');
    //         if(prdt.length){
    //             $scope.product=prdt[0].data[0][1];

    //         }
    //     }
    // }
    self.onResize();
}

self.onResize = function() {
    $('.mat-form-field-appearance-outline .mat-form-field-outline').css('color',self.ctx.widgetConfig.color);
    $('.mat-form-field-appearance-outline .mat-form-field-label').css('color',self.ctx.widgetConfig.color);
    $('.mat-focus-indicator .mat-icon-button .mat-button-base').css('color',self.ctx.widgetConfig.color);
    $('.mat-form-field-wrapper').css('padding','0px');
    $('.mat-button-wrapper').css('color',self.ctx.widgetConfig.color);
    $('.mat-select-arrow').css('color',self.ctx.widgetConfig.color);
    $('.mat-select-min-line').css('color',self.ctx.widgetConfig.color);
}

self.onDestroy = function() {}

