descriptor.controllerScript:
self.onInit = function() {  
    const $scope=self.ctx.$scope;
    $scope.width=self.ctx.width;
    $scope.height=self.ctx.height;
    $scope.settings=self.ctx.settings;
    $scope.title = self.ctx.widgetConfig.title;
    self.ctx.chartgj = new CanvasJS.Chart('batgltp1', {
        backgroundColor: 'transparent',
    	animationEnabled: true,
    	width: $scope.width*0.65,
    	height: $scope.height-94,
    	title: {
    		text: ""
    	},
    	data: [{
    	    indexLabelFontColor: self.ctx.widgetConfig.color,
    		type: "doughnut",
    		innerRadius: "70%",
    		showInLegend: false,
    		legendText: "{label}",
    		indexLabel: "{label}: #percent%",
    		dataPoints: [
    			{label: 'Label', y: 34}
    		]
    	}]
    });
    self.ctx.chartgj.render();
    self.ctx.calculationFactors={};
  self.onResize();
}

self.onDataUpdated = function() {
    const $scope=self.ctx.$scope;

    self.ctx.chartgj.data[0].dataPoints.length=0;
    self.ctx.chartgj.render();
    unit='';
    if(Object.keys(self.ctx.calculationFactors).length){
        if(self.ctx.data.length){
            if(self.ctx.data[0].data.length){
                aliasDatagj=[];
                aliases=[];
                self.ctx.data.forEach(td=>{
                    if(aliases.indexOf(td.datasource.aliasName)==-1){
                        aliases.push(td.datasource.aliasName);
                        total=0;
                        td.data.forEach(tdd=>{
                            total+=tdd[1];
                        })
                        aliasDatagj.push({name: td.datasource.aliasName, data:total, color: td.dataKey.color});
                    }else{
                        total=0;
                        td.data.forEach(tdd=>{
                            total+=tdd[1];
                        })
                        i=aliases.indexOf(td.datasource.aliasName);
                        aliasDatagj[i].data+=total;
                    }
                });

                aliasDatagj.forEach(aData=>{
                    if(aData.name.toLowerCase()=='furnace return'){
                        findex=aliasDatagj.findIndex(adt=>adt.name.toLowerCase()=='furnace oil')
                        if(findex!=-1){
                            aliasDatagj[findex].data-=aData.data;
                        }
                    }
                    if(aData.name.toLowerCase()=='diesel return'){
                        findex=aliasDatagj.findIndex(adt=>adt.name.toLowerCase()=='diesel')
                        if(findex!=-1){
                            aliasDatagj[findex].data-=aData.data;
                        }
                    }
                })
                aliasDatagj=aliasDatagj.filter(ad=>ad.name.toLowerCase()!='furnace return')
                aliasDatagj=aliasDatagj.filter(ad=>ad.name.toLowerCase()!='diesel return')
                aliasDatagj.forEach(dts=>{
                    if(dts.name.toLowerCase()=='electricity'){
                        dts.data=dts.data*Number(self.ctx.calculationFactors.electricityGj);
                    }
                    if(dts.name.toLowerCase()=='diesel'){
                        dts.data=dts.data*Number(self.ctx.calculationFactors.dieselOilGj);
                    }
                    if(dts.name.toLowerCase()=='furnace oil'){
                        dts.data=dts.data*Number(self.ctx.calculationFactors.furnaceOilGj);
                    }
                });

                $scope.aliasDatagj=aliasDatagj;
                self.ctx.detectChanges();
                aliasDatagj.forEach((dt, index)=>{
                    self.ctx.chartgj.data[0].dataPoints.push({label: dt.name, y:Number(dt.data.toFixed(2)), color: dt.color});
                });
                self.ctx.chartgj.render();
            }
        }
    }else{
        self.ctx.attributeService.getEntityAttributes({entityType: 'CUSTOMER', id: '24fb6cf0-cad7-11ed-8b01-738772f9cc16'}, 'SERVER_SCOPE',[self.ctx.settings.calculateFactorKey], {}).subscribe(resp=>{
            cf=resp[0].value;
            cf.sort((a, b) => (a.year < b.year) ? 1: -1);
            self.ctx.calculationFactors=cf[0];
            self.onDataUpdated();
        });
    }
}
self.onDestroy=function(){
    self.ctx.chartgj.destroy();
}

self.onResize = function() {
    const $scope=self.ctx.$scope;
    $scope.width=self.ctx.width;
    $scope.height=self.ctx.height;
    self.ctx.detectChanges();
}

