descriptor.controllerScript:
self.onInit = function() {
    self.ctx.deviceList={
        transformer_0: 'cd9b7fa0-d5cf-11ed-afaa-333332abe82d', //WZP DCL
        transformer_1: '1bfc57b0-cae4-11ed-8b01-738772f9cc16',
        transformer_2: '1d463cd0-cae4-11ed-8b01-738772f9cc16',
        transformer_3: '1e8f5ea0-cae4-11ed-8b01-738772f9cc16',
        transformer_4: '1fd8ce90-cae4-11ed-8b01-738772f9cc16',
        solar_1: 'f3471620-1d77-11ee-8bc6-01b3484f4adf',
        solar_2: 'f31298a0-1d77-11ee-8bc6-01b3484f4adf',
        solar_3: 'f37ddd90-1d77-11ee-8bc6-01b3484f4adf',
        solar_4: 'f334edb0-1d77-11ee-8bc6-01b3484f4adf',
        solar_5: 'f3236180-1d77-11ee-8bc6-01b3484f4adf',
        solar_6: 'f3902d10-1d77-11ee-8bc6-01b3484f4adf',
        solar_7: 'f3591780-1d77-11ee-8bc6-01b3484f4adf',
        solar_8: 'f36bb520-1d77-11ee-8bc6-01b3484f4adf',
        solar_9: '3730cf80-1d7c-11ee-8bc6-01b3484f4adf',

        generator_1: '6181dbf0-d068-11ed-8b01-738772f9cc16',
        generator_2: '6349cab0-d068-11ed-8b01-738772f9cc16',
        generator_3: '6513dc50-d068-11ed-8b01-738772f9cc16',
        generator_4: '66de1500-d068-11ed-8b01-738772f9cc16',
        generator_5: 'dd86f7f0-d5cf-11ed-afaa-333332abe82d',
        lt_3: '21221770-cae4-11ed-8b01-738772f9cc16',           //Line 1 consumption
        lt_4: '158e8790-cae4-11ed-8b01-738772f9cc16',           //Line 1 consumption
        lt_5: '16d670e0-cae4-11ed-8b01-738772f9cc16',           //Line 2 consumption
        ats: '196977d0-cae4-11ed-8b01-738772f9cc16',            //Office and utility
        base_load: '1ab2c0b0-cae4-11ed-8b01-738772f9cc16',      //Office and utility
        drf_line_2: '181fe0d0-cae4-11ed-8b01-738772f9cc16',     //Line 2 consumption
        compressor_1: 'ceddeab0-d5cf-11ed-afaa-333332abe82d',   //Compressor
        compressor_2: 'd0270c80-d5cf-11ed-afaa-333332abe82d',   //Compressor
        compressor_dryer: 'd1707c70-d5cf-11ed-afaa-333332abe82d',//Compressor
    }
    const token = localStorage.getItem('jwt_token');    
    const $scope=self.ctx.$scope;
    $scope.isLoading=true;
    $scope.deviceList=self.ctx.deviceList;
    $scope.lineStrokeColor='#00bedd';
    $scope.stepBoxBgColor='blue'
    $scope.energyUnit='kWh';
    $scope.dataMode='history';
    $scope.color=self.ctx.widgetConfig.color;
    $scope.settings=self.ctx.settings;
    $scope.reportTypes=['Today', 'Daily', 'Monthly', 'Yearly', 'Custom']
    $scope.today=new Date();
    let selectType = "Today";
    $scope.selectType = selectType;
    $scope.selectedType='Today';
    let selectedStartDate,selectedEndDate;
    let energyActionDescriptor = self.ctx.actionsApi.getActionDescriptors('viewEnergyDetails');
    $scope.energyActionDescriptor=energyActionDescriptor;
    if($('.step-box')){
        $('.step-box').each(function(index){
            $(this).click(function(event){
                if(energyActionDescriptor){
                    if($(this).data('nodeid')){ 
                        dvc=self.ctx.data.filter(dvcD=>dvcD.datasource.entity.id.id==$(this).data('nodeid'));
                        self.ctx.actionsApi.handleWidgetAction(event, energyActionDescriptor[0], {entityType: 'DEVICE', id: $(this).data('nodeid')}, dvc[0].datasource.entityLabel);
                    }
                }
            });
            $(this).hover((event)=>{
                if($(this).data('nodeid')){
                    $(this).addClass("hover");
                }
            }, ()=>{
                $(this).removeClass("hover");
            });
        })
    }
    let r=0;
    self.ctx.s=setInterval(function(){
        r+=10
        $("#svg").css('transform', 'rotate('+r+'deg)');
        // $("#svg2").css('transform', 'rotate('+r+'deg)')
        if(r==100)r=10;
    }, 100);

    let actionDescriptor = self.ctx.actionsApi.getActionDescriptors('viewDetails');
    $scope.actionDescriptor=actionDescriptor;

    $scope.openDatePicker=function(dp) {
        dp.open();
    }

    $scope.selectStartDate=function(eventData, dp, format) {
        selectedStartDate = eventData;
        $("#startDate").val(moment(eventData).format(format));

        loadGeneratorData(selectedStartDate.getTime(), selectedStartDate.setMonth(selectedStartDate.getMonth()+1));
        self.ctx.detectChanges();
        dp.close();
    }
    $scope.startDateChanege=function($event){
        selectedStartDate = $event.value;
        startDt=new Date(moment(selectedStartDate).format('YYYY-MM-DD 06:00:00'));
        if(selectType=='Daily'){
            loadGeneratorData(startDt.getTime(), startDt.setDate(startDt.getDate()+1));
        }else if(selectType=='Custom'){
            if(selectedEndDate){
                endDt=new Date(moment(selectedEndDate).format('YYYY-MM-DD 06:00:00'));
                loadGeneratorData(startDt.getTime(), endDt.setDate(endDt.getDate()+1));
            }
        }
        self.ctx.detectChanges();
    }
    $scope.endDateChanege=function($event) {
        selectedEndDate = $event.value;
        if(selectedStartDate){
            startDt=new Date(moment(selectedStartDate).format('YYYY-MM-DD 06:00:00'));
            endDt=new Date(moment(selectedEndDate).format('YYYY-MM-DD 06:00:00'));
            loadGeneratorData(startDt.getTime(), endDt.setDate(endDt.getDate()+1));
        }
        self.ctx.detectChanges();
    }
    $scope.selectYear=function($event, dp, format){
        start=new Date(moment($event).format('YYYY-MM-DD 06:00:00'));
        $("#startDate").val(moment($event).format(format));
        loadGeneratorData(start.getTime(), start.setFullYear(start.getFullYear()+1));
        dp.close();
    }
    $scope.typeSelect = function($event){
        selectType = $event.value;
        $scope.selectedType=selectType;
        if(selectType=='Today'){
            loadGeneratorData(new Date(moment(new Date()).format('YYYY-MM-DD 00:00:00')).getTime(), new Date().getTime());
        }
    }

    $scope.toggleMonitoring=function(){
        $scope.dataMode=$scope.dataMode=='history'?'realtime':'history';
        if($scope.dataMode=='history'){
            $scope.energyUnit='kWh'
            loadGeneratorData(new Date(moment(new Date()).format('YYYY-MM-DD 06:00:00')).getTime(), new Date().getTime());
        }else{
            $scope.selectedType='Today';
            $scope.selectType='Today';
            $scope.energyUnit='kW';
            self.onDataUpdated();
        }
        self.ctx.detectChanges();
    }
    $scope.data={};
    loadGeneratorData(new Date(moment(new Date()).format('YYYY-MM-DD 06:00:00')).getTime(), new Date().getTime());
    function loadGeneratorData(startTs, endTs){
        $scope.isLoading=true;
        $scope.data={};
        $scope.l1_consumption=0;
        $scope.l2_consumption=0;
        $scope.officeAndUtility=0;
        $scope.compressorTotal=0;
        $scope.generatorTotal=0;
        $scope.renewableTotal=0;
        $scope.busbarTotal=0;
        $scope.solarTotal=0;

        let transformerTotal=0;
        let generatorTotal=0;
        let l1cons=0;
        let l2cons=0;
        let officeAndUtility=0;
        let compressorTotal=0;
        let busbarTotal=0;
        let solarTotal=0;
        Object.keys(self.ctx.deviceList).forEach((key, index)=>{
            const req=self.ctx.http.post('/nrdapi/get_daily_kwh',  {start_ts: startTs, end_ts: endTs, entity_id: self.ctx.deviceList[key]},  {headers: {"X-Authorization": 'Bearer '+token}});
            req.subscribe(res=>{
                if(index==Object.keys(self.ctx.deviceList).length-1){
                    $scope.isLoading=false;
                }
                cTotal=0;
                if(res.length){
                    if(key.split('_')[0]=='transformer' && key!='transformer_0'){
                        res.forEach(r=>{
                            cTotal+=r.net_kwh;
                            transformerTotal+=r.net_kwh;
                            busbarTotal+=r.net_kwh;
                        });
                    }else if(key.split('_')[0]=='generator'){
                        res.forEach(r=>{
                            cTotal+=r.net_kwh;
                            generatorTotal+=r.net_kwh;
                            busbarTotal+=r.net_kwh;
                        });
                    }else if(key=='transformer_0'){
                        res.forEach(r=>{
                            cTotal+=r.net_kwh;
                        });
                    }else if(key.split('_')[0]=='solar'){
                        res.forEach(r=>{
                            solarTotal+=r.net_kwh;
                            busbarTotal+=r.net_kwh;
                        });
                    }else{
                        res.forEach(r=>{
                            cTotal+=r.net_kwh;
                            l1cons+=key=='lt_3'||key=='lt_4'?r.net_kwh:0;
                            l2cons+=key=='lt_5'||key=='drf_line_2'?r.net_kwh:0;
                            officeAndUtility+=key=='base_load'?r.net_kwh:0;
                            compressorTotal+=key=='compressor_1'||key=='compressor_2'||key=='compressor_dryer'?r.net_kwh:0;
                        });

                    }

                    $scope.l1_consumption=Number(l1cons.toFixed(2));
                    $scope.l2_consumption=Number(l2cons.toFixed(2));
                    $scope.officeAndUtility=officeAndUtility.toFixed(2);
                    $scope.compressorTotal=compressorTotal.toFixed(2);
                    $scope.generatorTotal=generatorTotal.toFixed(2);
                    self.ctx.detectChanges();
                }
                $scope.busbarTotal=busbarTotal.toFixed(2);
                $scope.renewableTotal=solarTotal.toFixed(2);
                $scope.data[key]=cTotal.toFixed(2);
                self.ctx.detectChanges();
            }, error=>{
                if(index==Object.keys(self.ctx.deviceList).length-1){
                    $scope.isLoading=false;
                }
                $scope.data[key]=0.00;
                self.ctx.detectChanges();
            });
        });
    }
    self.onResize();
}
self.onDataUpdated=function(){
    $scope=self.ctx.$scope;
    if($scope.dataMode=='realtime'){
        $scope.l1_consumption=0;
        $scope.l2_consumption=0;
        $scope.officeAndUtility=0;
        $scope.compressorTotal=0;
        $scope.generatorTotal=0;
        $scope.renewableTotal=0;
        $scope.busbarTotal=0;
    }
    if(self.ctx.data.length){
        if(self.ctx.data[0].data.length){
            Object.keys(self.ctx.deviceList).forEach(key=>{
                dvc=self.ctx.data.filter(dvcD=>dvcD.datasource.entity.id.id==self.ctx.deviceList[key]);
                if(dvc.length){
                    if($scope.dataMode=='realtime'){
                        const cdata=Number(Number(dvc[0].data[0][1]).toFixed(2));
                        $scope.data[key]=cdata;
                        $scope.l1_consumption=Number(($scope.l1_consumption+(key=='lt_3'||key=='lt_4'?cdata:0)).toFixed(2));
                        $scope.l2_consumption=Number(($scope.l2_consumption+(key=='lt_5'||key=='drf_line_2'?cdata:0)).toFixed(2));
                        $scope.compressorTotal=Number(($scope.compressorTotal+(key=='compressor_1'||key=='compressor_2'||key=='compressor_dryer'?cdata:0)).toFixed(2));
                        $scope.officeAndUtility=Number(($scope.officeAndUtility+(key=='base_load'?cdata:0)).toFixed(2));
                        $scope.busbarTotal=Number(($scope.busbarTotal+(key=='generator_1'||key=='generator_2'||key=='generator_3'||key=='generator_4'||key=='generator_5'||key=='transformer_1'||key=='transformer_2'||key=='transformer_3'||key=='transformer_4'||key.split('_')[0]=='solar'?cdata:0)).toFixed(2));
                        $scope.renewableTotal=Number(($scope.renewableTotal+(key.split('_')[0]=='solar'?cdata:0)).toFixed(2));

                        self.ctx.detectChanges();

                    }
                }
            });
            self.ctx.detectChanges();
        }
    }
}
self.onResize=function(){
    const $scope=self.ctx.$scope;
    $scope.height=self.ctx.height-94;
    $scope.deviceWidth=self.ctx.width;
    $scope.width=(self.ctx.width<1920?1920:self.ctx.width)-12;
    // $scope.width=self.ctx.width-12;

    $scope.box1w=200;
    $scope.box2w=200;
    $scope.busbarw=$scope.width*0.3;
    $scope.box3w=250
    $scope.connectorHeight=80;
    self.ctx.detectChanges();
}
self.onDestroy=function(){
    clearInterval(self.ctx.s);
}
self.actionSources = function() {
     return {
         'viewEnergyDetails': {name: 'View energy details',multiple: false}
     };
 }

