    chartOptions = {
    backgroundColor: 'transparent',
    width: $scope.width,
    height: $scope.height,
    animationEnabled: true,
    title: {
      text: ""
    },
    axisX:{
        title: "",
        labelFontColor: self.ctx.widgetConfig.color
    },
    axisY: {
      title: "",
      prefix: "",
      suffix: "kWh",
      lineThickness: 0.5,
      gridThickness: 0,
      labelFontColor: self.ctx.widgetConfig.color,
      gridColor: "white",
      includeZero: true
    },
    data: [{
        indexLabelFontColor: self.ctx.widgetConfig.color,
      type: "waterfall",
      yValueFormatString: "#,##0kWh",
      dataPoints: [
        { label: "Generation",  y: 1 ,color: '#50AF47'},
        { label: "From the grid", y: 0, color: '#EF7D00'},
        { label: "To the grid", y: 0, color: '#AFCA0B'},
        { label: "Total Consumption", isIntermediateSum: true, color: '#FBBA00'}
      ]
    }]
  }
  self.ctx.chart = new CanvasJS.Chart('waterfallChart', chartOptions);
    self.ctx.chart.render();
}
self.onDataUpdated=function(){
    const $scope=self.ctx.$scope;
    if(self.ctx.data.length){
            aliasDatagj=[];
            aliases=[];
            self.ctx.data.forEach(td=>{
                if(aliases.indexOf(td.datasource.aliasName)==-1){
                    aliases.push(td.datasource.aliasName);
                    total=0;
                    td.data.forEach(tdd=>{
                        total+=tdd[1];
                    })
                    aliasDatagj.push({name: td.datasource.aliasName, data:total, color: td.dataKey.color});
                }else{
                    total=0;
                    td.data.forEach(tdd=>{
                        total+=tdd[1];
                    })
                    i=aliases.indexOf(td.datasource.aliasName);
                    aliasDatagj[i].data+=total;
                }
            });
            aliasDatagj.forEach((adg, i)=>{
                self.ctx.chart.data[0].dataPoints[i].y=adg.data;
            });
            self.ctx.chart.render();

    }
}

self.onResize=function(){
    const $scope=self.ctx.$scope;
    $scope.height=self.ctx.height-40;
    $scope.width=self.ctx.width;
}

