descriptor.controllerScript:
self.onInit = function() {
    const $scope=self.ctx.$scope;
    $scope.tablePagination = {
            pageIndex: 0,
            pageSize: 5,
            pageSizeOptions: [5, 10, 15, 20],
            handler: (pagination) => {
                const { pageIndex, pageSize } = pagination;
                if (pageSize !== undefined) {
                    $scope.tablePagination.pageSize = pageSize;
                }
                if (pageIndex !== undefined) {
                    $scope.tablePagination.pageIndex = pageIndex;
                }
            },
            getFrom: () => {
                const { pageIndex, pageSize } = $scope.tablePagination;
                return pageIndex * pageSize;
            },
            getTo: () => {
                const { pageIndex, pageSize } = $scope.tablePagination;
                return pageIndex * pageSize + pageSize;
            }
        }
    $scope.height=self.ctx.height;
    $scope.color=self.ctx.widgetConfig.color;
    const $injector = $scope.$injector;
    const token = localStorage.getItem('jwt_token');
    const customDialog = $injector.get(self.ctx.servicesMap.get('customDialog'));
    $scope.leftContent=[
        {title: 'MAIN POWER', id: '2cf90030-d2ab-11ed-8b01-738772f9cc16'},
        {title: 'GENERATOR POWER', id: '71f2ff00-d54a-11ed-afaa-333332abe82d'},
        {title: 'FURNACE OIL', id: 'ac3cada0-d71b-11ed-afaa-333332abe82d'},
        {title: 'COMPRESSED AIR', id: '39e12280-d85c-11ed-afaa-333332abe82d'},
        {title: 'WATER PROCESS', id: 'abe16a40-f222-11ed-afaa-333332abe82d'}
    ];
    $scope.rightContent=[
        {title: 'DASHBOARD', id: 'd4af2280-fab7-11ed-afaa-333332abe82d'},
        {title: 'ANALYTICS', id: '9d38fcb0-0780-11ee-afaa-333332abe82d'}
    ];
    $scope.handleLeftClick=function(id){
        self.ctx.router.navigate(['/dashboards/'+id]);
    }
    $scope.gotoProduction=function($event){
        infoDialogDataTemplate=`<div aria-label="Info" style="width: 800px; height: 100%;">
        <mat-toolbar fxLayout="row"><div>Data entry</div><span fxFlex></span>
        <button mat-stroked-button color="primary" (click)="addProduction()" style="float: right"><span *ngIf="!showForm">+ Add new entry</span><span *ngIf="showForm">Show entries</span></button>
                <button mat-icon-button (click)="close()">
                    <mat-icon>close</mat-icon>
                </button>
            </mat-toolbar>
            <div mat-dialog-content>
                <div *ngIf="!showForm && tablePagination">
                    <table mat-table [dataSource]="data.productionData.slice(tablePagination?.getFrom(), tablePagination?.getTo())" class="mat-elevation-z8">
                      <ng-container matColumnDef="date">
                        <th mat-header-cell *matHeaderCellDef style="width: 30%"> Date </th>
                        <td mat-cell *matCellDef="let element"> {{element.date}} </td>
                      </ng-container>
                      <ng-container matColumnDef="value">
                        <th mat-header-cell *matHeaderCellDef style="width: 30%"> Quantity (KG) </th>
                        <td mat-cell *matCellDef="let element"> {{element.value}} </td>
                      </ng-container>
                      <ng-container matColumnDef="loadshedding_ms">
                        <th mat-header-cell *matHeaderCellDef style="width: 30%"> Load shedding time </th>
                        <td mat-cell *matCellDef="let element"> {{showTimeInHMS(element.loadshedding_ms)}} </td>
                      </ng-container>
                      <ng-container matColumnDef="action">
                        <th mat-header-cell *matHeaderCellDef> Action </th>
                        <td mat-cell *matCellDef="let element"> <button mat-icon-button (click)="editProductionEntry(element)"><mat-icon>edit</mat-icon></button> </td>
                      </ng-container>
                      <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
                        <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
                    </table>
                    <mat-paginator
                    [length]="data.productionData.length"
                    [pageSize]="tablePagination?.pageSize"
                    [pageSizeOptions]="tablePagination?.pageSizeOptions"
                    (page)="tablePagination?.handler($event)"
                    aria-label="Select page"
                    showFirstLastButtons="true">
                </mat-paginator>
                </div>
                <form class="example-form" *ngIf="showForm" [formGroup]="form" (ngSubmit)="save()">
                    <mat-form-field appearance="outline">
                        <mat-label>Date</mat-label>
                        <input matInput formControlName="sts" [matDatepicker]="picker">
                        <mat-datepicker-toggle matIconSuffix [for]="picker" style="position: absolute; right:0; top:0;"></mat-datepicker-toggle>
                        <mat-datepicker #picker></mat-datepicker>
                    </mat-form-field>
                    <mat-form-field appearance="outline">
                        <mat-label>Production quantity (KG)</mat-label>
                        <input matInput formControlName="dbl_v" type="number" id="stopReason" required>
                        <mat-error *ngIf="form.controls['dbl_v'].hasError('required')">Field is required</mat-error>
                        <mat-error *ngIf="form.controls['dbl_v'].hasError('min')">Production can't be negetive</mat-error>
                    </mat-form-field>
                    <label>Load shedding time</label>
                    <div style="display: flex;">
                        <mat-form-field  appearance="outline">
                            <mat-label>Hour</mat-label>
                            <input matInput type="number" formControlName="hour" placeholder="HH" required>
                            <mat-error *ngIf="form.controls['hour'].hasError('required')">Field is required</mat-error>
                            <mat-error *ngIf="form.controls['hour'].hasError('min')">Hour can't be negetive</mat-error>
                        </mat-form-field>
                        <div style="padding: 10px; font-size: 35px">:</div>
                        <mat-form-field appearance="outline">
                            <mat-label>Minute</mat-label>
                            <input matInput type="number" placeholder="MM" formControlName="minute" required>
                            <mat-error *ngIf="form.controls['minute'].hasError('required')">Field is required</mat-error>
                            <mat-error *ngIf="form.controls['minute'].hasError('min')">Minute can't be negetive</mat-error>
                        </mat-form-field>
                    </div>
                    <button mat-flat-button color="primary" id="stopBtn">Save</button>
                </form>
            </div>

        </div>`;
        data=[];
        const req=self.ctx.http.post('/nrdapi/get_production_data', {start_ts:new Date(moment('2023-01-01')).getTime(), end_ts: new Date().getTime()}, {headers: {"X-Authorization": 'Bearer '+token}});
        req.subscribe(resd=>{
            resd.sort((a,b)=>b.ts-a.ts)
            resd.forEach(r=>{
                data.push({date: moment(new Date(Number(r.ts))).format('YYYY-MM-DD'), value: r.dbl_v, loadshedding_ms: r.loadshedding_ms});
            });
            customDialog.customDialog(infoDialogDataTemplate, InfoDialogController, {productionData: data}).subscribe(
                function(res) {
                    if (res) {
                    }
                }
            );
        }, error=>{
            customDialog.customDialog(infoDialogDataTemplate, InfoDialogController, {productionData: data}).subscribe(
                function(res) {
                    if (res) {
                    }
                }
            );
        });
    }
    function InfoDialogController(instance) {
        vm=instance;

        vm.showForm=false;
        vm.displayedColumns=['date', 'value','loadshedding_ms','action'];
        vm.tablePagination=$scope.tablePagination;
        vm.form=vm.fb.group({
            sts: [new Date()],
            dbl_v:['', [vm.validators.required, vm.validators.min(0)]],
            hour: ['', [vm.validators.required, vm.validators.min(0)]],
            minute: ['', [vm.validators.required, vm.validators.min(0)]]
        });
        vm.close = function (){
            vm.dialogRef.close(null);
        }
        vm.editProductionEntry=function(element){
            let hour=(Math.floor((Number(element.loadshedding_ms)/1000)/3600));
            let minute=Math.round(((Number(element.loadshedding_ms)/1000)%3600)/60);
            vm.form.patchValue({
                sts: element.date,
                dbl_v: element.value,
                hour: hour,
                minute: minute
            });
            vm.showForm=true;
        }
        vm.save=function(){
            if(vm.form.valid){
                val=vm.form.value;
                let postData={
                    sts: moment(vm.form.value.sts).format('YYYY-MMM-DD'),
                    dbl_v: vm.form.value.dbl_v,
                    loadshedding_ms: (Number(vm.form.value.hour)*60+Number(vm.form.value.minute))*60*1000
                }
                const req=self.ctx.http.post('/nrdapi/update_production_data', postData, {headers: {"X-Authorization": 'Bearer '+token}});
                req.subscribe(res=>{
                    self.ctx.showToast('success', 'Data is saved!', 2000, 'top', 'start',  self.ctx.$scope.toastTargetId);
                    vm.dialogRef.close(null);
                    $scope.gotoProduction()
                });
            }
        }
        vm.showTimeInHMS=function(ms){
            let hours=Math.floor(ms/(3600*1000));
           let rest=ms%(3600*1000);
           let minutes=Math.round(rest/(60*1000));
           second=rest%(60*1000);
           seconds=Math.round(second/1000);
           let buildString='';

           buildString+=hours>9?hours:'0'+hours;
           buildString+=':';
           buildString+=minutes>9?minutes:'0'+minutes;
           buildString+=':';
           buildString+=seconds>9?seconds:'0'+seconds;
           return buildString;
        }
        vm.addProduction=function(){
            vm.form.reset();
            vm.showForm=!vm.showForm;
        }
    }
}
self.onResize=function(){
    const $scope=self.ctx.$scope;
    $scope.height=self.ctx.height;
    self.ctx.detectChanges();
}

