descriptor.controllerScript:
self.onInit = function() {
    $scope=self.ctx.$scope;
    $scope.settings=self.ctx.settings;
    $scope.section='form';
    let decoded = JSON.parse(atob(localStorage.getItem('jwt_token').split('.')[1]));
    let userFullName = decoded.firstName + " " + decoded.lastName;
    $scope.deviceInformation=[];
    $scope.displayedColumns=['furnaceOilTonne','furnaceOilGj','furnaceOilTco2','dieselOilTonne','dieselOilGj','dieselOilTco2','electricityGj','electricityTco2']
    data=self.ctx.defaultSubscription.data[0]
    self.ctx.getWidgetPositions = function(){
        let pathNames = window.location.pathname.split("/");
        return {
            isInWidgetEditor: pathNames.includes("widget-editor"),
            isInWidgetBundle: pathNames.includes("widgets-bundles"),
            isInDashboard: pathNames.includes("dashboards"),
            isInDashboardEditor: self.ctx.isEdit
        }
    }
    $scope.buildForm=function(data){
       $scope.form=$scope.fb.group({
            year:[data?data.year:new Date(), $scope.validators.required],
            furnaceOilTonne: [data?data.furnaceOilTonne:0.00096, $scope.validators.required],
            furnaceOilGj: [data?data.furnaceOilGj:0, $scope.validators.required],
            furnaceOilTco2: [data?data.furnaceOilTco2:0, $scope.validators.required],
            dieselOilTonne: [data?data.dieselOilTonne:0.00086, $scope.validators.required],
            dieselOilGj: [data?data.dieselOilGj:0, $scope.validators.required],
            dieselOilTco2: [data?data.dieselOilTco2:0, $scope.validators.required],
            electricityGj: [data?data.electricityGj:0, $scope.validators.required],
            electricityTco2: [data?data.electricityTco2:0,$scope.validators.required],
            intensityFactor: [data?data.intensityFactor:0, $scope.validators.required]
        }); 
        self.ctx.detectChanges();
        $('#startDate').val(data.year);
    }
    let gwp=self.ctx.getWidgetPositions();
    if(!gwp.isInWidgetBundle){
        self.ctx.attributeService.getEntityAttributes(data.datasource.entity.id, 'SERVER_SCOPE', [$scope.settings.attributeName]).subscribe(function(data){
            if(data.length>0){
                $scope.deviceInformation=data[0].value;
                $scope.dataSource=data[0].value;
                console.log(data)
                data=data[0].value[data[0].value.length-1];
                $scope.buildForm(data);
            }else{
                $scope.buildForm(null);
            }
        }, error=>{
            $scope.buildForm(null);
        });
    }else{
        $scope.buildForm(null);
    }
    self.ctx.$scope.openDatePicker=function(dp) {
        dp.open();
    }
    $scope.selectYear=function($event, picker, format='YYYY'){
        $("#startDate").val(moment($event).format(format));
        $scope.form.get('year').setValue(moment($event).format(format));
        picker.close();
    }
    $scope.saveInformation=function(){
        if ($scope.form.valid) {
            if($scope.deviceInformation.length){
                infos=$scope.deviceInformation;
                ifs=infos.filter(inf=>moment(inf.year).format('YYYY')==moment($scope.form.value.year).format('YYYY'));
                if(ifs.length){
                    index=infos.indexOf(ifs[0]);
                    $scope.deviceInformation.splice(index,1);
                }
            }
            $scope.form.value.historyDate=new Date();
            $scope.form.value.updatedBy=userFullName;

            $scope.deviceInformation.push($scope.form.value);
            self.ctx.attributeService.saveEntityAttributes(
            data.datasource.entity.id, 'SERVER_SCOPE', [{key:$scope.ctx.settings.attributeName, value:$scope.deviceInformation}]).subscribe(function(e){
                self.onInit();
                self.ctx.showToast('success', 'Device information is saved successfully', 2000, 'top', 'start',  self.ctx.$scope.toastTargetId);
            });
        }
    }

}

