descriptor.controllerScript:
self.onInit = function() {
    self.ctx.deviceList={
        truckUnload: 'f224c4a0-dce9-11ed-afaa-333332abe82d',                //FM_13001
        FurnaceOilFlowBoiler2In: 'f5cd3330-dce9-11ed-afaa-333332abe82d',    //FM_13004
        FurnaceOilFlowBoiler1In:'f35cf680-dce9-11ed-afaa-333332abe82d',     //FM_13002
        FurnaceOilFlowBoiler1Rtn:'f4950150-dce9-11ed-afaa-333332abe82d',    //FM_13003
        FurnaceOilFlowBoiler2Rtn:'f7976be0-dce9-11ed-afaa-333332abe82d',    //FM_13005
        FeedWaterBoilerIn: '9b616fb0-ee28-11ed-afaa-333332abe82d',
        FeedWaterBoiler1In: '9b61bdd0-ee28-11ed-afaa-333332abe82d',
        FeedWaterBoiler2In: '9b61e4e4-ee28-11ed-afaa-333332abe82d',
        CondensedReturnLine1: '9b61bdd2-ee28-11ed-afaa-333332abe82d',
        CondensedReturnLine2: '9b61e4e2-ee28-11ed-afaa-333332abe82d',
        SteamBoiler1Output: '9b61e4e3-ee28-11ed-afaa-333332abe82d',
        SteamBoiler2Output: '9b61e4e0-ee28-11ed-afaa-333332abe82d',
    };
    self.ctx.runtimeDeviceList={
        boiler1: '331d8355-ee2b-11ed-afaa-333332abe82d',     //RT_52001
        boiler2: '331cc001-ee2b-11ed-afaa-333332abe82d'     //RT_52002
    };
    self.ctx.tempAndPressDeviceList={
        temparature_feedWaterTank: '331d8350-ee2b-11ed-afaa-333332abe82d',  //TP_32001
        tempPressBoiler1: '331d3530-ee2b-11ed-afaa-333332abe82d',            //TP_32002
        tempPressBoiler2: '331d0e20-ee2b-11ed-afaa-333332abe82d'            //TP_32003
    }
    const token = localStorage.getItem('jwt_token');
    const $scope=self.ctx.$scope;
    $scope.runtime={};
    $scope.isLoading=true;
    $scope.deviceList=self.ctx.deviceList;
    $scope.lineStrokeColor='#00bedd';
    $scope.stepBoxBgColor='blue'
    $scope.energyUnit='kWh';
    $scope.flowUnit='L';
    $scope.steamUnit='kg';
    $scope.waterUnit='m3';
    $scope.dataMode='history';

    $scope.color=self.ctx.widgetConfig.color;
    $scope.settings=self.ctx.settings;
    $scope.reportTypes=['Today', 'Daily', 'Monthly', 'Yearly', 'Custom']
    $scope.today=new Date();
    let selectType = "Today";
    $scope.selectType = selectType;
    $scope.selectedType='Today';
    let selectedStartDate,selectedEndDate;
    if($('.step-box')){
        $('.step-box').each(function(index){
            $(this).click(function(event){
                    if(actionDescriptor){
                        if($scope.dataMode=='realtime'){
                            self.ctx.actionsApi.handleWidgetAction(event, actionDescriptor[0], {entityType: 'DEVICE', id: $(this).data('nodeid')}, 'Device Name');
                        }
                    }
                })
        })
    }
    let actionDescriptor = self.ctx.actionsApi.getActionDescriptors('viewDetails');
    $scope.actionDescriptor=actionDescriptor;

    $scope.openDatePicker=function(dp) {
        dp.open();
    }

    $scope.selectStartDate=function(eventData, dp, format) {
        selectedStartDate = eventData;
        $("#startDate").val(moment(eventData).format(format));

        loadGeneratorData(selectedStartDate.getTime(), selectedStartDate.setMonth(selectedStartDate.getMonth()+1));
        self.ctx.detectChanges();
        dp.close();
    }
    $scope.startDateChanege=function($event){
        selectedStartDate = $event.value;
        startDt=new Date(moment(selectedStartDate).format('YYYY-MM-DD 06:00:00'));
        if(selectType=='Daily'){
            loadGeneratorData(startDt.getTime(), startDt.setDate(startDt.getDate()+1));
        }else if(selectType=='Custom'){
            if(selectedEndDate){
                endDt=new Date(moment(selectedEndDate).format('YYYY-MM-DD 06:00:00'));
                loadGeneratorData(startDt.getTime(), endDt.setDate(endDt.getDate()+1));
            }
        }
        self.ctx.detectChanges();
    }
    $scope.endDateChanege=function($event) {
        selectedEndDate = $event.value;
        if(selectedStartDate){
            startDt=new Date(moment(selectedStartDate).format('YYYY-MM-DD 06:00:00'));
            endDt=new Date(moment(selectedEndDate).format('YYYY-MM-DD 06:00:00'));
            loadGeneratorData(startDt.getTime(), endDt.setDate(endDt.getDate()+1));
        }
        self.ctx.detectChanges();

    }
    $scope.selectYear=function($event, dp, format){
        start=new Date(moment($event).format('YYYY-MM-DD 06:00:00'));
        $("#startDate").val(moment($event).format(format));
        loadGeneratorData(start.getTime(), start.setFullYear(start.getFullYear()+1));
        dp.close();
    }
    $scope.typeSelect = function($event){
        selectType = $event.value;
        $scope.selectedType=selectType;
        if(selectType=='Today'){
            loadGeneratorData(new Date(moment(new Date()).format('YYYY-MM-DD 00:00:00')).getTime(), now.getTime());
        }
    }

    $scope.toggleMonitoring=function(){
        $scope.dataMode=$scope.dataMode=='history'?'realtime':'history';
        if($scope.dataMode=='history'){
            $scope.flowUnit='L';
            $scope.energyUnit='kWh'
            $scope.steamUnit='kg';
            $scope.waterUnit='m3';
            loadGeneratorData(new Date(moment(new Date()).format('YYYY-MM-DD 06:00:00')).getTime(), new Date().getTime());
        }else{
            $scope.selectedType='Today';
            $scope.selectType='Today';
            $scope.flowUnit='L/h';
            $scope.energyUnit='kW';
            $scope.steamUnit='kg/h';
            $scope.waterUnit='m3/h';
            self.onDataUpdated();
        }
        self.ctx.detectChanges();
    }
    $scope.data={};
    Object.keys(self.ctx.deviceList).forEach(key=>{
        $scope.data[key]=0.00;
    })
    loadGeneratorData(new Date(moment(new Date()).format('YYYY-MM-DD 06:00:00')).getTime(), new Date().getTime());
    function loadGeneratorData(startTs, endTs){
        $scope.isLoading=true;
        Object.keys(self.ctx.deviceList).forEach((key, index)=>{
            const req=self.ctx.http.post('/nrdapi/get_daily_consumption',  {start_ts: startTs, end_ts: endTs, entity_id: self.ctx.deviceList[key]},  {headers: {"X-Authorization": 'Bearer '+token}});
            req.subscribe(res=>{
                if(index==Object.keys(self.ctx.deviceList).length-1){
                    $scope.isLoading=false;
                }
                let total=0;
                if(res.length){
                    res.forEach(r=>{
                        total+=r.net_kwh;
                    });
                }
                $scope.data[key]=Number(total.toFixed(2));
                self.ctx.detectChanges();
            }, error=>{
                if(index==Object.keys(self.ctx.deviceList).length-1){
                    $scope.isLoading=false;
                }
                $scope.data[key]=0.00;
                self.ctx.detectChanges();
            });
        });

        loadTemparatureAndPressure(startTs, endTs);
        loadBoilerRuntime(startTs, endTs);
    }
    function loadTemparatureAndPressure(startTs, endTs){
        $scope.tempAndPress={};
        Object.keys(self.ctx.tempAndPressDeviceList).forEach((key, index)=>{
            if($scope.tempAndPress[key]==undefined){
                $scope.tempAndPress[key]={};
            }
            self.ctx.http.get('/api/plugins/telemetry/DEVICE/'+self.ctx.tempAndPressDeviceList[key]+'/values/timeseries?keys=ts_temperature,ts_pressure&endTs='+endTs+'&startTs='+startTs+'&interval='+(endTs-startTs)+'&agg=AVG', {headers: {"X-Authorization": 'Bearer '+token}}).subscribe(response=>{
                if(response.ts_temperature!=undefined){
                    $scope.tempAndPress[key]['temperature']=Number(response.ts_temperature[0].value).toFixed(2);
                }
                if(response.ts_pressure!=undefined){
                    $scope.tempAndPress[key]['pressure']=Number(response.ts_pressure[0].value).toFixed(2);
                }
            });
        });
    }
    function loadBoilerRuntime(startTs, endTs){
        $scope.boilerRuntime={};
        Object.keys(self.ctx.runtimeDeviceList).forEach((key, index)=>{
            if($scope.boilerRuntime[key]==undefined){
                $scope.boilerRuntime[key]='';
            }
            self.ctx.http.get('/api/plugins/telemetry/DEVICE/'+self.ctx.runtimeDeviceList[key]+'/values/timeseries?keys=ts_deltaUnit&endTs='+endTs+'&startTs='+startTs+'&interval='+(endTs-startTs)+'&agg=SUM', {headers: {"X-Authorization": 'Bearer '+token}}).subscribe(response=>{
                if(response.ts_deltaUnit!=undefined){
                    second=Number(response.ts_deltaUnit[0].value);
                    hour=Math.floor(second/3600);
                    restS=second%3600;
                    minutes=Math.floor(restS/60);
                    sec=Math.floor(restS%60);
                    bs=(hour>9?hour:'0'+hour)+':'+(minutes>9?minutes:'0'+minutes)+':'+(sec>9?sec:'0'+sec);
                    $scope.boilerRuntime[key]=bs
                }
            });
        });
    }
    $(function() {
        $(".step-box").contextmenu(function(e) {
            deleteAllElementByClassName();

            // ==========================
            if($(this).data('nodeid')==undefined){
                return true;
            }else{
                tableContent='';
                key=$(this).data('nodeid');
                if($scope.tempAndPress[key]!=undefined){

                    if($scope.tempAndPress[key]['temperature']!=undefined){
                        tableContent+='<tr>';
                        tableContent+='<td>Temperature</td><td>: '+$scope.tempAndPress[key]['temperature']+'</td>';
                        tableContent+='</tr>';
                    }
                    if($scope.tempAndPress[key]['pressure']!=undefined){
                        tableContent+='<tr>';
                        tableContent+='<td>Pressure</td><td>: '+$scope.tempAndPress[key]['pressure']+'</td>';
                        tableContent+='</tr>';
                    }

                    if(key=='tempPressBoiler1'){
                        tableContent+='<tr>';
                        tableContent+='<td>Runtime</td><td>: '+$scope.boilerRuntime['boiler1']+'</td>';
                        tableContent+='</tr>';
                    }
                    if(key=='tempPressBoiler2'){
                        tableContent+='<tr>';
                        tableContent+='<td>Runtime</td><td>: '+$scope.boilerRuntime['boiler2']+'</td>';
                        tableContent+='</tr>';
                    }
                }
                let x = (parseInt(e.pageX)-parseInt($(this).offset().left)) + 'px';
                let y = (parseInt(e.pageY)-parseInt($(this).offset().top)) + 'px';
                let innerDiv = $(`
                    <div class="inner-content">
                        <div class="lds-ellipsis"><div></div><div></div><div></div><div></div></div>
                        <table>
                        ${tableContent}
                        </table>
                    </div>
                `);

                innerDiv.css({
                  "background-color": "black",
                  "color":"white",
                  "font-size":"16px",
                  "opacity":0.8,
                  "width": "auto",
                  "height": "80px",
                  "padding": "5px",
                  "border-radius":"4px",
                  "display": "flex",
                  "align-items": "center"
                });


                let div = $('<div class="content">').css({
                  "position": "absolute",
                //   "left": x,
                  "top": y,
                  "overflow": 'hidden',
                  "z-index": "100",
                  "text-align": "left"
                });
                div.append(innerDiv);
                $(this).append(div);


                let contentwidth = $(".content").width(); //Here your image width
                let contentheight = $(".content").height(); // Here your image height

                if((parseInt(e.pageX)+contentwidth)> $('body').width()){
                    x = (parseInt(e.pageX)-parseInt($(this).offset().left))-contentwidth;
                }
                if((parseInt(e.pageY)+contentheight)> $('body').height())
                    y=(parseInt(e.pageY)-parseInt($(this).offset().top))-contentheight;
                $(".content").css({
                    // "left": x,
                    "top": y
                });
                return false;

            }
      });
      $("body").click(function(e){
            deleteAllElementByClassName();
      })
    });
    const deleteAllElementByClassName = () => {
        // delete all existing content
        let elements = document.querySelectorAll('.content');

        // Iterate over the elements and remove them from their parent
        elements.forEach(function(element) {
            let parent = element.parentNode;
            parent.removeChild(element);
        });
    }
    self.onResize();
}

self.onDataUpdated=function(){
    $scope=self.ctx.$scope;
    $scope.runtime={};
    if(self.ctx.data.length){
        if(self.ctx.data[0].data.length){
            if($scope.dataMode=='realtime'){
                Object.keys(self.ctx.deviceList).forEach((key, index)=>{
                    dvc=self.ctx.data.filter(dvcD=>dvcD.datasource.entity.id.id==self.ctx.deviceList[key]);
                    if(dvc.length){
                        $scope.data[key]=Number(Number(dvc[0].data[0][1]).toFixed(2));
                        self.ctx.detectChanges();
                    }else{
                        $scope.data[key]=0;
                        self.ctx.detectChanges();
                    }
                });
            }
            Object.keys(self.ctx.runtimeDeviceList).forEach((key, index)=>{
                dvc=self.ctx.data.filter(dvcD=>dvcD.datasource.entity.id.id==self.ctx.runtimeDeviceList[key]);
                if(dvc.length){
                    $scope.runtime[key]=dvc[0].data[0][1]=='true';
                    self.ctx.detectChanges();
                }
            });
        }
    }
}
self.onResize=function(){
    const $scope=self.ctx.$scope;
    $scope.height=self.ctx.height-94;
    $scope.deviceWidth=self.ctx.width;
    $scope.width=(self.ctx.width<1200?1200:self.ctx.width)-12;
    $scope.sboxw=self.ctx.width<1200?200:250;
    self.ctx.detectChanges();
}
self.actionSources = function() {
     return {
         'viewDetails': {name: 'View details',multiple: false}
     };
 }

