descriptor.controllerScript:
self.onInit = function() {
    const $scope=self.ctx.$scope;
    self.ctx.deviceList={
        boreholeWithdrawal: '9b58bd20-ee28-11ed-afaa-333332abe82d', //Borehole Withdrawal
        wtpInlet: '9b5a6ad0-ee28-11ed-afaa-333332abe82d',           //WTP Inlet
        waterSocialUse1: '9b5a6ad1-ee28-11ed-afaa-333332abe82d',     //Social Use
        waterSocialUse2: '9b5b2e20-ee28-11ed-afaa-333332abe82d',     //Social Use
        waterROReject: '9b53db21-ee28-11ed-afaa-333332abe82d',      //Social use, RO Reject Utilization, RO Reject
        waterInBoiler: '9b616fb0-ee28-11ed-afaa-333332abe82d',      //Boiler use
        condensedRtnLine1: '9b61bdd2-ee28-11ed-afaa-333332abe82d',   //Boiler use, condensed Return
        condensedRtnLine2: '9b61e4e2-ee28-11ed-afaa-333332abe82d',   //Boiler use, condensed Return
        waterROTreated: '9b53db20-ee28-11ed-afaa-333332abe82d',      //Boiler use, RO Accept
        boiler1: '9b61bdd0-ee28-11ed-afaa-333332abe82d',
        boiler2: '9b61e4e4-ee28-11ed-afaa-333332abe82d',
        processWater: '9b61bdd3-ee28-11ed-afaa-333332abe82d',
        processWaterLine1: '9b61e4e1-ee28-11ed-afaa-333332abe82d',
        processWaterLine2: '9b61bdd1-ee28-11ed-afaa-333332abe82d',
        processSteamLine1: '9b61e4e3-ee28-11ed-afaa-333332abe82d',
        processSteamLine2: '9b61e4e0-ee28-11ed-afaa-333332abe82d',
        etpInlet: '9b540230-ee28-11ed-afaa-333332abe82d',
        roDischarge: 'b99225c0-f86d-11ed-afaa-333332abe82d'
    }
    $scope.isLoading=true;
    const token = localStorage.getItem('jwt_token');
    $scope.deviceList=self.ctx.deviceList;
    $scope.lineStrokeColor='#00bedd';
    $scope.stepBoxBgColor='blue'
    $scope.unit='m3';
    $scope.steamUnit='kg'
    $scope.dataMode='history';

    $scope.showLayer2=false;
    $scope.color=self.ctx.widgetConfig.color;
    $scope.settings=self.ctx.settings;
    $scope.reportTypes=['Today', 'Daily', 'Monthly', 'Yearly', 'Custom']
    $scope.today=new Date();
    let selectType = "Today";
    $scope.selectType = selectType;
    $scope.selectedType='Today';
    let selectedStartDate,selectedEndDate;
    if($('.step-box')){
        $('.step-box').each(function(index){
            $(this).click(function(event){
                if(actionDescriptor){
                    if($(this).data('nodeid')){
                        dvc=self.ctx.data.filter(dvcD=>dvcD.datasource.entity.id.id==$(this).data('nodeid'));
                        self.ctx.actionsApi.handleWidgetAction(event, actionDescriptor[0], {entityType: 'DEVICE', id: $(this).data('nodeid')}, dvc[0].datasource.entityLabel);
                    }
                }
            });
            $(this).hover((event)=>{
                if($(this).data('nodeid')){
                    $(this).addClass("hover");
                }
            }, ()=>{
                $(this).removeClass("hover");
            });
        })
    }


    let actionDescriptor = self.ctx.actionsApi.getActionDescriptors('viewDetails');
    $scope.actionDescriptor=actionDescriptor;

    $scope.openDatePicker=function(dp) {
        dp.open();
    }

    $scope.selectStartDate=function(eventData, dp, format) {
        selectedStartDate = eventData;
        $("#startDate").val(moment(eventData).format(format));
        loadGeneratorData(selectedStartDate.getTime(), selectedStartDate.setMonth(selectedStartDate.getMonth()+1));
        self.ctx.detectChanges();
        dp.close();
    }

    $scope.selectYear=function($event, dp, format) {
        start=new Date(moment($event).format('YYYY-MM-DD 06:00:00'));
        $("#startDate").val(moment($event).format(format));
        loadGeneratorData(start.getTime(), start.setFullYear(start.getFullYear()+1));
        self.ctx.detectChanges();
        dp.close();
    }
    $scope.startDateChanege=function($event){
        selectedStartDate = $event.value;
        startDt=new Date(moment(selectedStartDate).format('YYYY-MM-DD 06:00:00'));
        if(selectType=='Daily'){
            loadGeneratorData(startDt.getTime(), startDt.setDate(startDt.getDate()+1));
        }else if(selectType=='Custom'){
            if(selectedEndDate){
                endDt=new Date(moment(selectedEndDate).format('YYYY-MM-DD 06:00:00'));
                loadGeneratorData(startDt.getTime(), endDt.setDate(endDt.getDate()+1));
            }
        }
        self.ctx.detectChanges();
    }
    $scope.endDateChanege=function($event) {
        selectedEndDate = $event.value;
        if(selectedStartDate){
            startDt=new Date(moment(selectedStartDate).format('YYYY-MM-DD 06:00:00'));
            endDt=new Date(moment(selectedEndDate).format('YYYY-MM-DD 06:00:00'));
            loadGeneratorData(startDt.getTime(), endDt.setDate(endDt.getDate()+1));
        }
        self.ctx.detectChanges();

    }
    $scope.typeSelect = function($event){
        selectType = $event.value;
        $scope.selectedType=selectType;
        if(selectType=='Today'){
            loadGeneratorData(new Date(moment(new Date()).format('YYYY-MM-DD 00:00:00')).getTime(), new Date().getTime());
        }
    }

    $scope.toggleLayer2=function(){
        if($scope.showLayer2){
            $scope.showLayer2=false;
        }else{
            $scope.showLayer2=true;
        }
        self.ctx.detectChanges();
    }
    $scope.toggleMonitoring=function(){
        $scope.dataMode=$scope.dataMode=='history'?'realtime':'history';
        if($scope.dataMode=='history'){
            $scope.unit='m3';
            $scope.steamUnit='kg'
            loadGeneratorData(new Date(moment(new Date()).format('YYYY-MM-DD 06:00:00')).getTime(), new Date().getTime());
        }else{
            $scope.selectedType='Today';
            $scope.selectType='Today';
            $scope.unit='m3/h';
            $scope.steamUnit='kg/h'
            self.onDataUpdated();
        }
        self.ctx.detectChanges();
    }
    $scope.data={};
    Object.keys(self.ctx.deviceList).forEach(key=>{
        $scope.data[key]=0.00;
    })
    loadGeneratorData(new Date(moment(new Date()).format('YYYY-MM-DD 06:00:00')).getTime(), new Date().getTime());
    function loadGeneratorData(startTs, endTs){
        $scope.isLoading=true;
        const allKeys=Object.keys(self.ctx.deviceList)
        allKeys.forEach((key, index)=>{
            const req=self.ctx.http.post('/nrdapi/get_daily_consumption',  {start_ts: startTs, end_ts: endTs, entity_id: self.ctx.deviceList[key]},  {headers: {"X-Authorization": 'Bearer '+token}});
            req.subscribe(res=>{
                if(index==Object.keys(self.ctx.deviceList).length-1){
                    $scope.isLoading=false;
                }
                let total=0;
                if(res.length){
                    res.forEach(r=>{
                        total+=r.net_kwh;
                    });
                }
                $scope.data[key]=Number(total.toFixed(2));
                self.ctx.detectChanges();
            }, error=>{
                if(index==Object.keys(self.ctx.deviceList).length-1){
                    $scope.isLoading=false;
                }
                $scope.data[key]=0.00;
                self.ctx.detectChanges();
            });
        });
    }
    $(function() {
        $(".step-box").contextmenu(function(e) {
            deleteAllElementByClassName();
            // ==========================
            if($(this).data('popup')==undefined && $(this).data('live') == undefined){
                return true;
            }else{
                let x = (parseInt(e.pageX)-parseInt($(this).offset().left)) + 'px';
                let y = (parseInt(e.pageY)-parseInt($(this).offset().top)) + 'px';
                let innerDiv=``;
                if($(this).data('popup')!=undefined){
                     innerDiv= $(`
                    <div class="inner-content">
                        <div class="lds-ellipsis"><div></div><div></div><div></div><div></div></div>
                        ${$(this).data('popup').split('+').join('<br>')}
                    </div>
                `);
                }else{
                    innerDiv=$(`<div class="inner-content">
                        <div class="lds-ellipsis"><div></div><div></div><div></div><div></div></div>
                        <div id="withdrawn" style="min-width: 275px">Loading...</div>
                        </div>`);
                }

                innerDiv.css({
                  "background-color": "black",
                  "color":"white",
                  "font-size":"16px",
                  "opacity":0.8,
                  "width": "auto",
                  "height": "80px",
                  "padding": "5px",
                  "border-radius":"4px",
                  "display": "flex",
                  "align-items": "center"
                });


                let div = $('<div class="content">').css({
                  "position": "absolute",
                //   "left": x,
                  "top": y,
                  "overflow": 'hidden',
                  "z-index": "100",
                  "text-align": "left",
                  "left": x
                });
                div.append(innerDiv);
                $(this).append(div);


                let contentwidth = $(".content").width(); //Here your image width
                let contentheight = $(".content").height(); // Here your image height

                if((parseInt(e.pageX)+contentwidth)> $('body').width()){
                    x = (parseInt(e.pageX)-parseInt($(this).offset().left))-contentwidth;
                }
                if((parseInt(e.pageY)+contentheight)> $('body').height())
                    y=(parseInt(e.pageY)-parseInt($(this).offset().top))-contentheight;
                $(".content").css({
                    // "left": x,
                    "top": y
                });
                return false;
            }
      });
      $("body").click(function(e){
            deleteAllElementByClassName();
      })
    });
    const deleteAllElementByClassName = () => {
        // delete all existing content
        let elements = document.querySelectorAll('.content');

        // Iterate over the elements and remove them from their parent
        elements.forEach(function(element) {
            let parent = element.parentNode;
            parent.removeChild(element);
        });
    }
    self.onResize();
}
self.onResize=function(){
    const $scope=self.ctx.$scope;
    $scope.height=self.ctx.height-94;
    $scope.deviceWidth=self.ctx.width;
    $scope.width=(self.ctx.width<1200?1200:self.ctx.width)-12;
    $scope.sboxw=200//self.ctx.width<1200?250:200;
    $scope.box2w=150;
    $scope.box3w=170;
    $scope.connectorHeight=90;
    self.ctx.detectChanges();
}
self.onDataUpdated=function(){
    $scope=self.ctx.$scope;
    if(self.ctx.data.length){
        if(self.ctx.data[0].data.length){
            waterVolumeData=self.ctx.data.filter(dt=>dt.dataKey.name=='waterVolume');
            if(waterVolumeData.length){
                let html='';
                waterVolumeData.forEach(volume=>{
                    html+='<div>'+volume.datasource.entityLabel+': '+Number(volume.data[0][1]).toFixed(2)+'m<sup>3</sup></div>'
                });
                $('#withdrawn').html(html);
                console.log(html)
            }
            if($scope.dataMode=='realtime'){
                Object.keys(self.ctx.deviceList).forEach(key=>{
                    dvc=self.ctx.data.filter(dvcD=>dvcD.datasource.entity.id.id==self.ctx.deviceList[key]);
                    if(dvc.length){
                        const cdata=Number(Number(dvc[0].data[0][1]).toFixed(2));
                        $scope.data[key]=cdata;
                        self.ctx.detectChanges();
                    }
                });
                self.ctx.detectChanges();
            }
        }
    }
}
self.onDestroy=function(){
    clearInterval(self.ctx.s);
}
self.actionSources = function() {
     return {
         'viewDetails': {name: 'View details',multiple: false}
     };
 }

