descriptor.controllerScript:
self.onInit = function() {
    const $scope = self.ctx.$scope;
    $scope.color=self.ctx.widgetConfig.color;
    $scope.width=self.ctx.width;
    $scope.today=new Date();
    $scope.type='today';
    $scope.startDate=null;
    $scope.endDate=null;
    $scope.currentDate = new Date();
    stdt=new Date(moment().format('YYYY-MM-DD 06:00:00'));
    if(stdt.getTime()>new Date().getTime()){
        stdt.setDate(stdt.getDate()-1);
    }
    self.ctx.timewindowFunctions.onUpdateTimewindow(stdt.getTime(), new Date().getTime());
    self.ctx.dbInterval=setInterval(() => {
        $scope.currentDate = new Date();
        self.ctx.detectChanges();
    }, 1000);
    $scope.openDatePicker=function(picker){
        picker.open();
    }
    $scope.dateChange=function($event, which){
        if($scope.startDate && $scope.endDate){
            edt=new Date(moment($scope.endDate).format('YYYY-MM-DD 06:00:00'));
            self.ctx.timewindowFunctions.onUpdateTimewindow(new Date(moment($scope.startDate).format('YYYY-MM-DD 06:00:00')).getTime(), edt.setDate(edt.getDate()+1));
        }
    }
    $scope.selectMonth=function($event, startPicker, format, which){
        if(which=='start'){
            $scope.startDate = $event;
            $("#startDate").val(moment($event).format(format));
        }else{
            $scope.endDate = $event;
            $("#endDate").val(moment($event).format(format));
        }
        if($scope.startDate&&$scope.endDate){
            endMonth=new Date($scope.endDate.getFullYear(), $scope.endDate.getMonth()+1, 0);
            self.ctx.timewindowFunctions.onUpdateTimewindow(new Date(moment($scope.startDate).format('YYYY-MM-01 06:00:00')).getTime(), new Date(moment(endMonth).format('YYYY-MM-DD 06:00:00')).getTime());
        }
        self.ctx.detectChanges();
        startPicker.close();
    }
    $scope.selectYear=function($event, startPicker, format, which){
        $scope.startDate = $event;
        $("#startDate").val(moment($event).format(format));
        if($scope.startDate){
            edt=new Date(moment($scope.startDate).format('YYYY-01-01 06:00:00'));
            self.ctx.timewindowFunctions.onUpdateTimewindow(new Date(moment($scope.startDate).format('YYYY-01-01 06:00:00')).getTime(), edt.setFullYear(edt.getFullYear()+1));
        }
        self.ctx.detectChanges();
        startPicker.close();
    }
    $scope.setClass=function($event, type){
        $scope.startDate=null;
        $scope.endDate=null;
        $('.daily').each(com=>{
            $($('.daily')[com]).removeClass('daily');
        });
        $($event.target).addClass('daily');
        if (type=='today') {
            stdt=new Date(moment().format('YYYY-MM-DD 06:00:00'));
            if(stdt.getTime()>new Date().getTime()){
                stdt.setDate(stdt.getDate()-1);
            }
            self.ctx.timewindowFunctions.onUpdateTimewindow(stdt.getTime(), new Date().getTime());
            self.ctx.detectChanges();
        }
        $scope.type=type;
    }
}

self.actionSources = function() {
    return {
        'elementClick': {
            name: 'widget-action.element-click',
            multiple: true
        }
    };
}

self.onDestroy = function() {
    clearInterval(self.ctx.dbInterval);
}
self.onResize=function(){
    const $scope=self.ctx.$scope;
    $scope.width=self.ctx.width;
