descriptor.controllerScript:
self.onInit = function() {  
    const $scope=self.ctx.$scope;
    $scope.width=self.ctx.width;
    $scope.height=self.ctx.height;
    $scope.settings=self.ctx.settings;
    $scope.title = self.ctx.widgetConfig.title;
    self.ctx.charttco = new CanvasJS.Chart('batgltp2', {
        backgroundColor: 'transparent',
    	animationEnabled: true,
    	width: $scope.width*0.65,
    	height: $scope.height-94,
    	title: {
    		text: ""
    	},
    	data: [{
    	    indexLabelFontColor: self.ctx.widgetConfig.color,
    		type: "doughnut",
    		innerRadius: "70%",
    		showInLegend: false,
    		legendText: "{label}",
    		indexLabel: "{label}: #percent%",
    		dataPoints: [
    			{label: 'Label', y: 34}
    		]
    	}]
    });
    self.ctx.charttco.render();
    self.ctx.calculationFactors={};
    self.onResize();
}

self.onDataUpdated = function() {
    const $scope=self.ctx.$scope;
    self.ctx.charttco.data[0].dataPoints.length=0;
    self.ctx.charttco.render();
    unit='';
    if(Object.keys(self.ctx.calculationFactors).length){
        if(self.ctx.data.length){
            if(self.ctx.data[0].data!=undefined){
                let aliasDatatco=[];
                let aliasestco=[];
                self.ctx.data.forEach(td=>{
                    let total=0;
                    if(aliasestco.indexOf(td.datasource.aliasName)==-1){
                        aliasestco.push(td.datasource.aliasName);

                        td.data.forEach(tdd=>{
                            total+=Number(tdd[1]);
                        })
                        aliasDatatco.push({name: td.datasource.aliasName, data:total, color: td.dataKey.color});
                    }else{
                        td.data.forEach(tdd=>{
                            total+=Number(tdd[1]);
                        })
                        i=aliasestco.indexOf(td.datasource.aliasName);
                        aliasDatatco[i].data+=total;
                    }
                });
                aliasDatatco.forEach(aData=>{
                    if(aData.name.toLowerCase()=='furnace return'){
                        findex=aliasDatatco.findIndex(adt=>adt.name.toLowerCase()=='furnace oil')
                        if(findex!=-1){
                            aliasDatatco[findex].data-=aData.data;
                        }
                    }
                    if(aData.name.toLowerCase()=='diesel return'){
                        findex=aliasDatatco.findIndex(adt=>adt.name.toLowerCase()=='diesel')
                        if(findex!=-1){
                            aliasDatatco[findex].data-=aData.data;
                        }
                    }
                });
                aliasDatatco=aliasDatatco.filter(ad=>ad.name.toLowerCase()!='furnace return')
                aliasDatatco=aliasDatatco.filter(ad=>ad.name.toLowerCase()!='diesel return')
                aliasDatatco.forEach(dts=>{
                    if(dts.name.toLowerCase()=='electricity'){
                        dts.data=dts.data*Number(self.ctx.calculationFactors.electricityTco2);
                    }
                    if(dts.name.toLowerCase()=='diesel'){
                        dts.data=dts.data*Number(self.ctx.calculationFactors.dieselOilTco2);
                    }
                    if(dts.name.toLowerCase()=='furnace oil'){
                        dts.data=dts.data*Number(self.ctx.calculationFactors.furnaceOilTco2);
                    }
                });
                $scope.aliasDatatco=aliasDatatco;

                aliasDatatco.forEach((dt, index)=>{
                    self.ctx.charttco.data[0].dataPoints.push({label: dt.name, y:Number(dt.data.toFixed(2)), color: dt.color});
                    // self.ctx.charttco.render();
                });
                self.ctx.charttco.render();
            }
        }
        self.ctx.detectChanges();
    }else{
        self.ctx.attributeService.getEntityAttributes({entityType: 'CUSTOMER', id: '24fb6cf0-cad7-11ed-8b01-738772f9cc16'}, 'SERVER_SCOPE',[self.ctx.settings.calculateFactorKey], {}).subscribe(resp=>{
            self.ctx.calculationFactors=resp[0].value[0];
            self.onDataUpdated();
        });
    }
}
self.onDestroy=function(){
    self.ctx.charttco.destroy();
}

self.onResize = function() {
    const $scope=self.ctx.$scope;
    $scope.width=self.ctx.width;
    $scope.height=self.ctx.height;
    self.ctx.detectChanges();
}

