descriptor.controllerScript:
self.onInit = function() { 
    const $scope=self.ctx.$scope;
    $scope.title=self.ctx.widgetConfig.title;
    self.onResize();

    self.ctx.calculationFactors={};
    self.ctx.attributeService.getEntityAttributes({entityType: 'CUSTOMER', id: '24fb6cf0-cad7-11ed-8b01-738772f9cc16'}, 'SERVER_SCOPE',[self.ctx.settings.calculateFactorKey], {}).subscribe(resp=>{
        cf=resp[0].value;
        cf.sort((a, b) => (a.year < b.year) ? 1: -1);
        self.ctx.calculationFactors=cf[0];
    });
}

self.onDataUpdated = function() {
    const $scope=self.ctx.$scope;
    const token = localStorage.getItem('jwt_token');
    if(self.ctx.data.length){
        aliasDatagj=[];
        aliases=[];
        self.ctx.data.forEach(td=>{
            if(aliases.indexOf(td.datasource.aliasName)==-1){
                aliases.push(td.datasource.aliasName);
                total=0;
                td.data.forEach(tdd=>{
                    total+=tdd[1];
                })
                aliasDatagj.push({name: td.datasource.aliasName, data:total, color: td.dataKey.color});
            }else{
                total=0;
                td.data.forEach(tdd=>{
                    total+=tdd[1];
                })
                i=aliases.indexOf(td.datasource.aliasName);
                aliasDatagj[i].data+=total;
            }
        });

        //Gigajoule calculation
        aliasDatagj.forEach(dts=>{
            if(dts.name.toLowerCase()=='electricity'){
                dts['gj']=dts.data*Number(self.ctx.calculationFactors.electricityGj);
                dts['tco2']=dts.data*Number(self.ctx.calculationFactors.electricityTco2);
            }
            if(dts.name.toLowerCase()=='diesel'){
                dts['gj']=dts.data*Number(self.ctx.calculationFactors.dieselOilGj);
                dts['tco2']=dts.data*Number(self.ctx.calculationFactors.dieselOilTco2);
                dts['tonne']=dts.data*Number(self.ctx.calculationFactors.dieselOilTonne);
            }
            if(dts.name.toLowerCase()=='furnace oil'){
                dts['gj']=dts.data*Number(self.ctx.calculationFactors.furnaceOilGj);
                dts['tco2']=dts.data*Number(self.ctx.calculationFactors.furnaceOilTco2);
                dts['tonne']=dts.data*Number(self.ctx.calculationFactors.furnaceOilTonne);
            }
        });
        intensityData={};
        let mce=0;
        const req=self.ctx.http.post('/nrdapi/get_production_data', {start_ts:self.ctx.timeWindow.minTime, end_ts: self.ctx.timeWindow.maxTime}, {headers: {"X-Authorization": 'Bearer '+token}});
        req.subscribe(resd=>{
            let totalProduction=0;
            resd.forEach(prdt=>{
                totalProduction+=Number(prdt.dbl_v);
            });
            mce=totalProduction*self.ctx.calculationFactors.intensityFactor;
            totalGj=0;
            totalTonne=0;
            aliasDatagj.forEach(adt=>{
                totalGj+=adt.gj!=undefined?adt.gj:0;
                totalTonne+=adt.tonne!=undefined?adt.tonne:0;
            });
            $scope.energyIntensity=Number((totalGj/mce).toFixed(2));
            $scope.tco2Intensity=Number((totalTonne/mce).toFixed(2));
            self.ctx.detectChanges();
        });
    }
}

self.onResize = function() {
    const $scope=self.ctx.$scope;
    $scope.height=self.ctx.height-38.22;
}

self.onDestroy = function() {
}

